INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('18','','零乐购电子商务平台 五年战略规划','2','0','','<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; 零乐购电子商务平台 五年战略规划</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第一年：全力以赴打造终端消费平台，实现销售业绩1000万，其中零售业绩504万元，一级批发业绩87万元，二级批发业绩128万元，积分消费业绩281万元，以武汉为中心辐射全国，建立15个物流配送站，100个配送点</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第二年：做好终端服务，提升终端市场造血能力，大力推动终端消费市场，确保全年销售业绩3000万元，其中零售业绩1512万元，一级批发业绩261万元，二级批发业绩384万元，积分消费业绩843万元，市场网络遍及全国25个省、直辖市，新建物流配送站45个，配送点100个，广告招商20个（存活），广告收入300万元</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第三年：加强终端管理，完善市场经营体系，极力冲刺销售业绩1亿元大关，其中零售业绩3360万元，一级批发业绩812万元，二级批发业绩768万元，积分消费业绩5060万元，市场网络遍及全国，新建物流配送站140个，配送点600个，成立5个商务管理中心（分公司筹备），广告招商80个（存活），广告收入2000万元</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第四年：完善企业管理，规范市场行为，努力达成销售业绩5亿元，其中零售业绩1.68亿元，一级批发业绩2900万元，二级批发市场业绩3840万元，，积分消费业绩2.646亿元，新建物流配送站500个，配送点3000个，全面实施跨行业，多领域联盟与合作，参股企业30家，控股企业5家，收购企业5家，广告招商收入1个亿，成立华东、华南、华中、华北、东北、东南、西北、西南8个片区大公司，打造集团公司平台，以实体经济亮剑，为下一年度新四版上市打下坚实基础</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第五年：鼓励和刺激终端消费，发展终端市场，实现全年目标业绩50亿元，其中零售业绩16.8亿元，一级批发业绩0.58亿元，二级批发业绩1.28亿元，积分消费31.34亿元，新建物流配送站1000个，配送点1万个，参股企业100家，控股企业20家，收购企业10家，成立分公司25家，壮实集团规模，完成新四版上市，融资20亿，广告招商收入15亿计，同时启动并开发海外市场，为集团进入创业板上市铺平道路</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp;公司秉承以人为本，服务社会，回报客户的经营理念，将广告招商收入的50%加权平分给终端消费客户（以广告点击积分为依据），10%作为慈善基金，20%作为市场流通费用，20%作为公司发展基金，当公司成功上市时，公司将给高管、市场总监、市场经理，市场主管、分销商、代理商配送原始股票</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp;创业艰辛苦难言，无限商机在眼前，让我们用热情的双手，诚挚地欢迎天下有识之士加盟&ldquo;零乐购&rdquo;，共铸我们更加美好的未来</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>','2','1','1401817272');
