INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('18','','零乐购电子商务平台 五年战略规划','2','0','','<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; 零乐购电子商务平台 五年战略规划</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第一年：全力以赴打造终端消费平台，实现销售业绩1000万，其中零售业绩504万元，一级批发业绩87万元，二级批发业绩128万元，积分消费业绩281万元，以武汉为中心辐射全国，建立15个物流配送站，100个配送点</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第二年：做好终端服务，提升终端市场造血能力，大力推动终端消费市场，确保全年销售业绩3000万元，其中零售业绩1512万元，一级批发业绩261万元，二级批发业绩384万元，积分消费业绩843万元，市场网络遍及全国25个省、直辖市，新建物流配送站45个，配送点100个，广告招商20个（存活），广告收入300万元</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第三年：加强终端管理，完善市场经营体系，极力冲刺销售业绩1亿元大关，其中零售业绩3360万元，一级批发业绩812万元，二级批发业绩768万元，积分消费业绩5060万元，市场网络遍及全国，新建物流配送站140个，配送点600个，成立5个商务管理中心（分公司筹备），广告招商80个（存活），广告收入2000万元</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">&nbsp;</span></strong></span></p>\r\n<p><span style=\"font-family: mceinline;\"><strong><span style=\"font-size: medium;\">第四年：完善企业管理，规范市场行为，努力达成销售业绩5亿元，其中零售业绩1.68亿元，一级批发业绩2900万元，二级批发市场业绩3840万元，，积分消费业绩2.646亿元，新建物流配送站500个，配送点3000个，全面实施跨行业，多领域联盟与合作，参股企业30家，控股企业5家，收购企业5家，广告招商收入1个亿，成立华东、华南、华中、华北、东北、东南、西北、西南8个片区大公司，打造集团公司平台，以实体经济亮剑，为下一年度新四版上市打下坚实基础</span></strong>