INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('52','','网店质量保证书','37','0','','<p><span style=\"font-size: medium;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 零乐购网店质量保证书&nbsp; </span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">我公司/网店（甲方）承诺在零乐购B2C平台销售的全部商品均符合国家法律法规及政府相关标准规定，并在零乐购B2C平台创建店铺前向零乐购电子商务平台、武汉市荣鑫华亚商贸有限公司（以下合称&ldquo;荣鑫华亚&rdquo;）提供与质量有关和市场销售相关证明文件（包括但不限于营业执照、生产许可证、卫生许可证、国家批准文号、经营许可证、品牌授权书等）。如前述证明文件的有效期已满，我公司/网店保证提前30日向零乐购告知，及时更新相关材料，并主动送达&ldquo;零乐购&rdquo;。如我公司/网店不能提供上述证明文件或未能及时更新，&ldquo;零乐购&rdquo;有权立即终止双方缔结的《零乐购B2C服务协议》、《零乐购B2C服务条款》，同时我公司/网店保证赔偿&ldquo;零乐购&rdquo;因此产生的全部损失。&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">&nbsp;1、我公司/网店（甲方）承诺向零乐购提供的相关证件（营业执照、卫生许可证号、批准文号等）均真实、有效，若我公司/网店提供的相关证件有伪造、变造的行为，一经查证，则我公司/网店保证向&ldquo;零乐购&rdquo;支付违约金壹万元，且我公司/网店保证承担全部法律责任及相应连带责任。&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">&nbsp;2、我公司/网店（甲方）保证做到商品流通各环节的质量控制，严把产品质量关，确保到消费者手上的商品安全可信，如有商品质量疑问，零乐购有权在不通知我公司的前提下，对我公司销售的某个批次的商品进行送检，相关费用由我公司承担。&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">&nbsp;3、如确因产品质量问题引起消费者投诉，我公司/网店（甲方）保证按照零乐购相关规则配合零乐购妥善处理，由此发生的全部费用和赔偿由我公司/网店承担，如情节严重或媒体曝光的，我公司/网店保证向零乐购支付违约金壹万元。零乐购有权单方面立即终止双方缔结的《零乐购B2C服务协议》、《零乐购B2C服务条款》，同时我公司/网店应赔偿零乐购由此造成的全部损失。&nbsp; </span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">4、我公司/网店（甲方）承诺在零乐购B2C平台销售的所有商品、店铺宣传内容均不侵犯任何第三方的任何知识产权（包括但不限于著作权、商标权、专利权），因此类情况发生的法律责任，全部由我公司/网店承担，并愿向零乐购支付违约金壹万元，如此类情况给零乐购带来经济损失，我公司/网店承诺足额进行赔偿。&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">&nbsp;5、如销售进口商品，我公司/网店（甲方）承诺所有销售的商品均为&ldquo;正品行货&rdquo;，零乐购有权对我公司/网店销售的商品进行抽检，并且要求我公司/网店能提供抽检样品的商检证明和卫生检验检疫证明，我公司/网店保证在5个工作日配合处理，如我公司/网店无法提供对应的相关证明文件，我公司/网店保证向&ldquo;零乐购&rdquo;支付违约金壹万元，零乐购有权追究我公司/网店相关责任，视情节轻重，零乐购有权单方面立即中止双方缔结的《零乐购B2C服务协议》、《零乐购B2C服务条款》，同时我公司/网店同意赔偿零乐购由此造成的全部损失。&nbsp;&nbsp; </span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">甲方公司/网店： </span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">甲方授权代表签字： </span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">甲方盖章：</span></p>','4','1','1403574801');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('53','','办理退款','37','0','','<p><span style=\"font-size: medium;\">\r\n<tbody style=\"widows: 2; text-transform: none; background-color: transparent; list-style-type: none; text-indent: 0px; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font: 12px/24px \'microsoft yahei\'; white-space: normal; orphans: 2; letter-spacing: normal; color: #666666; word-spacing: 0px; text-decoration: none; -webkit-text-size-adjust: none; -webkit-text-stroke-width: 0px; border: 0px; padding: 0px;\">\r\n</tbody>\r\n</span></p>\r\n<td style=\"background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</td>\r\n<p style=\"background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\" align=\"center\"><span style=\"font-size: medium;\">退款</span></p>\r\n<p style=\"line-height: 30px; background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">退款说明：</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">1.因商品质量问题或零乐购网店原因造成的，客户可整单退货，配送费及邮寄费将连同货款一并退还。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">2.如果订单中部分金额采用积分支付，在办理退货时，此积分仍退回。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 30px; background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">退款途径：</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">1.通过支付宝帐户退款：需要顾客提供经\"实名认证\"的支付宝账户名称，及对应的\"实名\"。两者要完全对应。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">2.若顾客无支付宝帐户或支付宝未经\"实名认证\"，请顾客提供可接收汇款的银行卡信息。信息内容包括：银行帐号，帐号对应的持卡人姓名，帐号所对应的开户行名称（如：XX省XX市XX银行XX开户行）</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">3.通过快钱退款：</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">快钱退款是按照用户付款时使用的付款方式来退的：</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">若用户付款时，使用的是快钱账户支付，则退款到快钱账户中；企业客户成功提交退款申请后，付款方将即时收到退款。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">若用户付款时，使用的是银行账户（银行卡）支付，则退款直接退到用户的银行账户（银行卡）中；企业客户成功提交退款申请后，快钱会在第二天进行退款。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">1-2天到帐：如果退款的订单是使用工、农、招、建、兴业、民生、浦发、深发银行卡付款的，一般情况下1-2个工作日就可以将款项退至相应银行卡中。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">10天到帐：如果是使用除上述以外的其他银行的银行卡付款的，退款时间会比较长，一般情况下10个工作日到账（最长为一个月）。</span></p>\r\n<p style=\"line-height: 30px; background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 30px; background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">补充说明：</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">用户银行卡内收到退款时，一般只能看到增加了一笔相应的金额，并不能看到这笔款项是从快钱退回的，暂时只有招商银行卡持卡人可以看到快钱交易号其中的10位数。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">对于长时间未收到退款款项的，请联系快钱清算中心（fsc@99bill.com）。</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">快钱退款到用户银行账户的情况是基于各个银行的操作时间，如果银行的操作时间有变化，快钱退款时间也会相应变化。</span></p>\r\n<p style=\"line-height: 30px; background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"line-height: 30px; background-color: transparent; list-style-type: none; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; font-weight: bold; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">退款时间：</span></p>\r\n<p style=\"line-height: 25px; background-color: transparent; list-style-type: none; text-indent: 2em; margin: 0px; outline-style: none; outline-color: invert; outline-width: 0px; font-family: \'microsoft yahei\'; text-decoration: none; -webkit-text-size-adjust: none; border: 0px; padding: 0px;\"><span style=\"font-size: medium;\">因为任何原因的退款，每天都可以为客户办理。我们将在收到顾客的退款信息后的2-3个工作日内进行退款，请顾客于2-3个工作日后进行查款。</span></p>\r\n<p><br class=\"Apple-interchange-newline\" /></p>','3','1','1403574852');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('54','','易货起源','39','0','','<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">易货，简单说，就是置换，是企业（个人）之间不用现金而进行的商品和服务的等价交换。企业用自有产品与对方交换，将产品售出，换回本应急需用现金购买的生产原材料及生活用品，从而增加销量，缓解资金压力，加快企业发展。<br />易货发展概况</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">易货交易可以追溯到人类最早的生产生活活动，如：1袋米换2只羊。在货币出现以前，人们早期的交易活动就是一种物物交换，它不需要货币作为交换媒介。</span></p>\r\n<p><span style=\"font-size: medium;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: medium;\">现代易货交易，就是基于因特网这个交易平台，利用易货额度(而不是现金)，打破时间和空间的限制，实现企业与企业之间与消费者之间商品或服务的自由交换。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />&nbsp;现代易货在我国才刚刚起步，而在欧美等发达国家，已经发展得非常成熟。据美国财政部声称，易货贸易已占到贸易额的27%&mdash;30%，易货在现代世界的经济中已占据重要的位置。从企业来看，世界500强企业80%已建立了易货部，现在在纽约股票交易所上市的65%的公司都在利用易货减少现金支出，增加销量，减少库存，开发新市场。</span></p>\r\n<p>&nbsp;</p>\r\n<p><br /><span style=\"font-size: medium;\">一、易货的概念<br /></span></p>\r\n<p><span style=\"font-size: medium;\">1.什么是易货？</span></p>\r\n<p><span style=\"font-size: medium;\">答：易货是指政府企业及个人之间对商品和服务的等值交换。</span></p>\r\n<p><span style=\"font-size: medium;\">2.什么是现代易货？</span></p>\r\n<p><span style=\"font-size: medium;\">答：现代易货从本质上讲，属于一种商品集市（Market Place）、易货银行（Barter Bank）和交易场（Exchange）的综合业务体系。</span></p>\r\n<p><span style=\"font-size: medium;\">3.什么是企业易货？</span></p>\r\n<p><span style=\"font-size: medium;\">答：企业与企业之间等值产品的互换，不涉及货币或补贴少量货币的交易，使采购需求与产品销售同步实现。</span></p>\r\n<p><span style=\"font-size: medium;\">4.易货公司是做什么的？</span></p>\r\n<p><span style=\"font-size: medium;\">答：易货公司是以其易货交易平台为依托，以丰富多样的产品或服务为基础，为企业提供非现金交易的B2B易货的专业公司。以解决企业的库存积压问题、多余的生产能力和服务空间的有效利用等等问题为己任，用优质的服务和良好的信誉为企业开辟新市场，打开新的销售渠道，增加额外销售额，节省现金，增加利润空间提供多方面的帮助和全套易货解决方案。</span></p>\r\n<p>&nbsp;</p>\r\n<p><br /><span style=\"font-size: medium;\">二、易货的发展历程<br /></span></p>\r\n<p><span style=\"font-size: medium;\">1901年ITEX在美国俄勒岗州创立，现代易货开始兴起，现时为美国最大的 B2B 易货贸易公司，并在该 NASDAQ (OTCBB)上市，总部设在华盛顿州 Bellevue。ITEX 每年处理二万四千名会员及90位特许经营商超过2.7亿美元的交易。</span></p>\r\n<p><span style=\"font-size: medium;\">20世纪80年代初，现代易货交易公司在美国、加拿大、澳大利亚等国普遍兴起。</span></p>\r\n<p><span style=\"font-size: medium;\">20世纪90年代，电子商务与传统易货概念的结合成为以高新技术改造传统企业的典范，是真正的&ldquo;鼠标加水泥&rdquo;。</span></p>\r\n<p><span style=\"font-size: medium;\">1991年，Bartercard在澳洲成立，现在是澳洲市场最大的易货公司，在16个国家拥有经营牌照，在全球有超过120个办事处。现有会员5万多个，其中2万多来自澳洲。2005年4月在伦敦的创业版(Alternative Investment Market)上市。</span></p>\r\n<p><span style=\"font-size: medium;\">1995年BBX转型为横跨三州的贸易协会（原为澳洲商业俱乐部），设立了&ldquo;BBX 商务平台&rdquo;。后融入了传统&ldquo;以物易物&rdquo;的概念转型成为&ldquo;BBX 商业易货平台&rdquo;，2005年5月BBX在澳洲股票交易所上市。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">1.国际易货现状</span></p>\r\n<p><span style=\"font-size: medium;\">易货被视为一种能够增加资产、进入新市场、增加利润和创造财富的工具，在国际间越来越受重视。据专家估计，易货交易占世界总交易达百分之二十到二十五。 世界500强企业80%进行易货，仅在美国，就有47万家公司进行易货，每年易货交易额达120亿美元。在纽约股票交易所上市公司中65%都在使用易货方式减少剩余库存，提高销售额，并使生产设备充分运转。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">2.中国易货现状</span></p>\r\n<p><span style=\"font-size: medium;\">中国的易货行业仍处于发芽阶段。我国加入WTO后，国内企业和国外的接触更为广泛，易货概念将迅速被企业接受，而对企业最为实惠的是：企业通过易货的方式交易，&ldquo;以物换物&rdquo;不产生现金流只记入库。</span></p>\r\n<p><br /><span style=\"font-size: medium;\">现代易货在发达国家的发展表明它有存在和发展的客观基础，在中国也一定有广阔的发展空间，我们正面临着一个巨大的商机。 上个世纪90年代初期被视作一个易货业腾飞的阶段，而21世纪初将是中国易货经济增长的黄金时期。<br /></span></p>','1','1','1403574877');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('55','','易货流程','39','0','','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img src=\"http://www.linglegou.com/data/files/mall/article/201406251350205789.jpg\" alt=\"易货流程.jpg\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">1.初次沟通：主动联系零乐购客服热线，或者点击在线客服，初步了解产品或服务；您有什么产品或服务？您需要什么产品或服务？</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">2.磋商确认：具体细节的协商和确认，包括产品，数量，价格，运输，售后服务等；</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">3.签订合同：根据谈判结果，双方确认后，如果是易出货物，货到后公司凭积分支付，如果是易入货物，直接用自己积分在零乐购下单购买纯积分产品，签订零乐购易货合同，如有需要，根据双方要求可签订各自传统的销售或服务合同；</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">4.履行合约：在交货过程中，可以委托物流或企业亲自送货，货到后双方对产品进行验收。<br /></span>\r\n<p>&nbsp;</p>\r\n</p>','2','1','1403574894');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('56','','易货结算','39','0','','<p><span style=\"font-size: medium;\">零乐购积分，是官方指定的易货交易支付介质，如果是易出货物，货到后公司凭积分支付，如果是易入货物，直接用自己积分在零乐购下单购买纯积分产品，签订零乐购易货合同，如有需要，根据双方要求可签订各自传统的销售或服务合同；<br /></span></p>','3','1','1403574921');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('57','','易货优势','39','0','','<p style=\"text-align: left; line-height: 22px; widows: 2; text-transform: none; list-style-type: none; font-variant: normal; font-style: normal; text-indent: 0px; margin: 0px; font-family: 宋体; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; font-weight: normal; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: medium;\">易货的优势：</span></p>\r\n<p style=\"text-align: left; line-height: 22px; widows: 2; text-transform: none; list-style-type: none; font-variant: normal; font-style: normal; text-indent: 0px; margin: 0px; font-family: 宋体; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; font-weight: normal; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: medium;\">&nbsp;</span></p>\r\n<p style=\"text-align: left; line-height: 22px; widows: 2; text-transform: none; list-style-type: none; font-variant: normal; font-style: normal; text-indent: 0px; margin: 0px; font-family: 宋体; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; font-weight: normal; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: medium;\">&nbsp;1、增加销量，开辟新的销售渠道。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;2、解决库存积压，盘活企业资产。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;3、使部分即将破产的企业盘活资产，东山再起。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;4、节省大量的销售成本。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;5、充分利用剩余产能。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;6、易其所需，节省现金支出。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;7、减少中间周转环节，减少产品损耗。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;8、因产量增加可在采购时压低价格。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;9、因产量增加带动就业岗位增加。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;10、提高产值，提升企业在行业内的地位和品牌价值。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;11、拉动消费，促进经济发展。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;12、扩大贸易机会，减少贸易摩擦。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;13、抑制通货膨胀。<br style=\"margin: 0px auto; padding: 0px;\" />&nbsp;14、带动物流等相关产业的发展。</span></p>','4','1','1403574947');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('58','','积分管理规则','35','0','','<p><span style=\"font-size: medium;\"><strong>各位亲爱的零乐购用户，零乐购将自2014年4月19日开业并启动积分体系，规则如下：&nbsp;</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">一、积分的赠送</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">1.免费注册用户即可每天赠送20分,积分由点广告形式发放&nbsp;<br /></span></p>\r\n<p><span style=\"font-size: medium;\">2.VIP用户每天赠送20分,分销商每天赠送50分,代理商每天赠送70分，每日点广告封顶,积分可以购物，可以内部转让，也可以兑换商品，以物换物，以货易货&nbsp;<br /></span></p>\r\n<p><span style=\"font-size: medium;\">3.积分可在商城消费不同产品具体查看产品规定。&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">二、积分的消费&nbsp;</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">1、免费注册用户获得的赠送积分，但未使用，将在下一个自然月自动扣除上月累计积分的一半，继续累计积分，仍未使用，将在下一个自然月继续自动扣除上月累计积分的一半，以此类推，以此循环&nbsp;<br /></span></p>\r\n<p><span style=\"font-size: medium;\">2、免费用户积分可以参与&ldquo;现金+积分&rdquo;的限量折扣抢购活动，无法转让积分，也无法购买纯积分产品<br /></span></p>\r\n<p><span style=\"font-size: medium;\">3、VIP、分销商、代理商的积分永不清零，永不作废，终身有效，购物，转进出均无限制</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: #ff0000;\"><strong>注：积分规则最终解释权归零乐购电子商务平台所有</strong></span></span></p>','5','1','1403625764');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('59','','关于“订单取消”问题的通知','2','0','','<p style=\"margin:0cm;margin-bottom:.0001pt\"><strong><span style=\"font-family: 宋体; font-weight: normal;\"><span style=\"font-family: mceinline;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></strong></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt\"><span style=\"font-family: mceinline;\"><strong><span style=\"font-family: 宋体; font-weight: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span><span style=\"font-weight: normal;\"><span style=\"font-family: mceinline;\">关于</span></span><span style=\"font-family: mceinline;\"><span style=\"font-weight: normal;\" lang=\"EN-US\">&ldquo;</span><span style=\"font-weight: normal;\">订单取消</span><span style=\"font-weight: normal;\" lang=\"EN-US\">&rdquo;</span><span style=\"font-weight: normal;\">问题的通知</span></span></strong></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt\"><strong><span style=\"font-weight: normal;\" lang=\"EN-US\"><span style=\"font-family: mceinline;\"><span style=\"font-family: mceinline;\">&nbsp;</span></span></span></strong></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt\"><span style=\"font-family: mceinline;\"><span style=\"font-family: mceinline;\"><strong><span style=\"font-weight: normal;\">各零乐购消费者：</span></strong></span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt\"><span lang=\"EN-US\"><span style=\"font-family: mceinline;\"><span style=\"font-family: mceinline;\"><strong>&nbsp;</strong></span></span></span></p>\r\n<p style=\"margin:0cm;margin-bottom:.0001pt\"><span lang=\"EN-US\"><span style=\"font-family: mceinline;\"><span style=\"font-family: mceinline;\"><strong>&nbsp;</strong></span></span></span></p>\r\n<p style=\"margin:0cm;m