; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span></strong></span></p>\r\n<p style=\"margin: 0cm 0cm 0.0001pt;\"><span style=\"font-family: mceinline;\"><span style=\"font-family: mceinline;\"><span style=\"font-family: mceinline;\"><strong></strong><strong><span style=\"font-weight: normal;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 零乐购技术部</span></strong></span></span></span></p>','1','1','1404171586');
INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('60','','关于我们','38','0','','<p class=\"MsoNormal\" align=\"left\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"http://www.linglegou.com/data/files/mall/article/201407012237267611.jpg\" alt=\"新版零乐购LOGO.jpg\" /></p>\r\n<p class=\"MsoNormal\" align=\"left\"><span style=\"font-size: 12pt; font-family: 宋体;\">零乐购（</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">www.linglegou.com</span><span style=\"font-size: 12pt; font-family: 宋体;\">）是武汉市荣鑫华亚商贸有限公司旗下网站，公司成立于</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">2010</span><span style=\"font-size: 12pt; font-family: 宋体;\">年，我们专注服务于多用户购物商城，免费购物，免费开店，免费易货，励志做中国最大最专业的积分换购交易平台，零乐购是以互联网</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">B2B</span><span style=\"font-size: 12pt; font-family: 宋体;\">、</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">B2C</span><span style=\"font-size: 12pt; font-family: 宋体;\">、</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">O2O</span><span style=\"font-size: 12pt; font-family: 宋体;\">为平台，将电子商务与超市，物流等用为一体的立体商务模式，其宗旨是打破传统代理制、缩短利润链接、开辟一条厂家与消费者对接的销售通路，</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&nbsp;</span><span style=\"font-size: 12pt; font-family: 宋体;\">其特点是网上信息、网下互联网实体店服务务，</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&nbsp;</span><span style=\"font-size: 12pt; font-family: 宋体;\">完成了空中、地面、跨行业、跨区域高效嫁接，</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&nbsp;</span><span style=\"font-size: 12pt; font-family: 宋体;\">零乐购实现了真正的异业联盟、也必将开启一个免费经济商业模式的新纪元</span></p>\r\n<p class=\"MsoNormal\" align=\"left\">&nbsp;</p>\r\n<p class=\"MsoNormal\" align=\"left\"><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\"><br /> </span><span style=\"font-size: 12pt; font-family: 宋体;\">零乐购把免费产品送到你的眼前，商品承诺七天无理由退换，我们致力于为消费者提供最简便，最实惠，最优质，最安全的免费购物乐趣。</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" align=\"left\"><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&nbsp; &nbsp; &nbsp; &nbsp;</span></p>\r\n<p class=\"MsoNormal\" align=\"left\"><span style=\"font-size: 12pt; font-family: 宋体;\">对于商家：</span></p>\r\n<p class=\"MsoNormal\" align=\"left\"><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\"><br /> </span><span style=\"font-size: 12pt; font-family: 宋体;\">零乐购为你牢牢锁定最优质的买家，依托</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&ldquo;</span><span style=\"font-size: 12pt; font-family: 宋体;\">赚积分</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&rdquo;</span><span style=\"font-size: 12pt; font-family: 宋体;\">广告展示区、</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&ldquo;</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\"><span style=\"font-family: 宋体;\">天天免费淘</span></span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&rdquo;</span><span style=\"font-size: 12pt; font-family: 宋体;\">等超强大关注板块，每天源源不断的给商家输送消费者，并带来数以万计的点击率和关注率，让准客户的被动式的关注和访问变成主动，帮助商家的闲置产品进行快速流通，能以物换物，以货易货，从而实现货物和资金的快速流通，让商家卖不动的产品卖得好，卖得好的产品卖得更好，零乐购商家的推广零风险，低成本，适合创业起步的，也适合代理商、分销商、</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">VIP</span><span style=\"font-size: 12pt; font-family: 宋体;\">用户开拓新用户，我们为你提供最好的交易体验，让订单暴增不再是空想。</span><span style=\"font-size: 12pt; font-family: mceinline;\" lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\" a