INSERT INTO ecm_article ( `article_id`, `code`, `title`, `cate_id`, `store_id`, `link`, `content`, `sort_order`, `if_show`, `add_time` ) VALUES  ('73','','律师声明','2','0','','<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\">&nbsp;</p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">受零乐购免费购物商城（运营商：武汉市荣鑫华亚商贸有限公司）的委托，武汉今天律师事务所律师石勇发表以下律师声明：武汉市荣鑫华亚商贸有限公司自主研发的免费购物、免费易货、免费开店<span lang=\"EN-US\">O2O</span>商业模式和技术在国家相关部委和社会各界的大力支持下，已在全国广泛推广应用。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">随着零乐购免费购物模式的推广，在所有零乐购人的辛勤努力下，市场已经遍布在黑龙江、江苏、安徽、山东、河北、河南、湖北、湖南、广东、贵州、陕西、江西、重庆市、北京市等地，但在实际运营过程中，也出现仿冒和侵权现象，而且愈演愈烈，严重侵犯了零乐购免费购物商城（运营商：武汉市荣鑫华亚商贸有限公司）所拥有的<span lang=\"EN-US\">O2O</span>商业模式技术知识产权。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">侵权零乐购的行为有以下几种形式：</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">一是侵犯零乐购的商标权，侵权企业将商标直接用于宣传资料和网页上，甚至在产品名称上直接使用商标标识。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">二是侵犯零乐购的互联网著作技术专利权。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">三是欺诈、冒充专利行为，主要是连续专利号造假，冒充专利产品。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">四是在网络和产品宣传区域使用我&ldquo;零乐购&rdquo;商标进行非法宣传和网站非法链接（比如：使用我&ldquo;零乐购&rdquo;链接到他们自己的与我&ldquo;零乐购&rdquo;同音不同字（也有同音同字）的网站，以期鱼目混珠。在搜索引擎中搜索&ldquo;零乐购&rdquo;查验其事实即知）。这种拙劣的、违反行业道德、违法侵犯商标权益的行为，终被行业和社会所不齿，亦为商业道德所不容。零乐购将保留向侵权企业和个人追究商标侵权责任的权力。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">侵权企业的行为对零乐购免费购物商城直接造成了合法权益的严重损害和经济利益的巨大损失。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">为此，本律师提出严正声明如下：</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">第一：武汉市荣鑫华亚商贸有限公司自主研发的零乐购免费购物、易货、开店算机著作技术的知识产权，由计算机著作开发设计专利和注册商标组成，其注册商标为：</span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-align: left; text-indent: 96pt; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan; mso-char-indent-count: 8.0;\" align=\"left\">&nbsp;<img src=\"http://www.linglegou.com/data/files/mall/article/201410111836293707.jpg\" alt=\"QQ图片20141011183432.jpg\" /></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">其他任何出现同音、同字、同形商标均属于侵权行为</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"font-family: 宋体;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\">第二：零乐购唯一官方网址：</span><span style=\"color: #2b2b2b; mso-bidi-font-size: 10.5pt;\" lang=\"EN-US\"><a href=\"http://www.linglegou.com/\"><span style=\"color: #0000ff; font-size: small;\">www.linglegou.com</span></a></span><span style=\"color: #2b2b2b; font-size: 10.5pt;\" lang=\"EN-US\">,</span><span style=\"color: #2b2b2b; font-size: 10.5pt;\">其它任何含&ldquo;<span lang=\"EN-US\">linglegou&rdquo;</span>又没有经过我方授权的网址均属假冒、侵权、模仿零乐购行为</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"font-family: 宋体;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\">第三：零乐购免费商城在国家工信部门依法取得</span><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\">《中华人民共和国电信与信息服务业务经营许可证》编号：</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\"><a href=\"http://www.miit.gov.cn/\" target=\"_blank\"><span style=\"color: #0000ff;\"><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\" lang=\"EN-US\"><span lang=\"EN-US\">鄂</span></span><span style=\"font-size: 12pt;\">ICP</span><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\" lang=\"EN-US\"><span lang=\"EN-US\">备</span></span><span style=\"font-size: 12pt;\">13013377</span><span style=\"font-family: 宋体; font-size: 12pt; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\" lang=\"EN-US\"><span lang=\"EN-US\">号</span></span><span style=\"font-size: 12pt;\">-2</span></span></a></span><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\"><span style=\"font-family: 宋体;\">，通过时间为</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\">2014</span><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\"><span style=\"font-family: 宋体;\">年</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\">3</span><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\"><span style=\"font-family: 宋体;\">月</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\">24</span><span style=\"font-family: 宋体;\"><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\">日，其他时间段注册的零乐购行为均属侵权行为</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\"><span style=\"font-family: 宋体;\">第四：武汉市荣鑫华亚商贸有限公司工商企业注册时间</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\">2010</span><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\"><span style=\"font-family: 宋体;\">年</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\">10</span><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\"><span style=\"font-family: 宋体;\">月，验证注册号码：</span></span><span style=\"font-family: &quot;Arial&quot;,&quot;sans-serif&quot;; color: #333333; font-size: 10.5pt;\" lang=\"EN-US\">420106000123928</span><span style=\"font-family: 宋体;\"><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\">，其他时间段注册的零乐购行为均属侵权行为</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"font-family: 宋体;\"><span style=\"color: #333333; font-size: 10.5pt; mso-bidi-font-family: Arial; mso-ascii-font-family: Arial; mso-hansi-font-family: Arial;\">第五：零乐购的商业模式定位非常清晰，只做免费购物、免费易货、免费开店，其他任何假冒零乐购非法经营行为对用户造成一切经济损失，与零乐购免费购物商城（运营商：武汉市荣鑫华亚商贸有限公司）无关</span></span><span style=\"color: #333333; font-size: 10.5pt; font-family: 宋体; line-height: 17.25pt; text-indent: 24pt;\">。</span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #333333; font-size: 10.5pt; font-family: 宋体; line-height: 17.25pt; text-indent: 24pt;\">第六：未经零乐购免费购物商城授权和允许，任何复制、下载或者转载零乐购版权的文字、图片、录音、视频等行为，均属于侵权行为</span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 21pt; background: white; mso-char-indent-count: 2.0;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">侵权企业为达到利目的，骗取用户信任、谎称其使用的是零乐购免费购物，易货，开店，假冒零乐购（<span lang=\"EN-US\">linglegou</span>）注册商标、谎称其已经得到专利授权的公司或企业的行为，已严重侵犯了零乐购免费商城的基本权益及其相关的知识产权。自本声明发布之日起，武汉市荣鑫华亚商贸有限公司将对侵权企业的侵权行为予以追究，直至要求其承担法律责任。</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">举报电话：<span lang=\"EN-US\">027-51116880</span>，传真：<span lang=\"EN-US\">027-86830588</span></span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">武汉今天律师事务所</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\"><span style=\"font-family: 宋体;\">石勇</span></span></p>\r\n<p style=\"text-justify: inter-ideograph; text-align: justify; line-height: 17.25pt; text-indent: 24pt; background: white;\"><span style=\"color: #2b2b2b; font-size: 10.5pt;\" lang=\"EN-US\"><span style=\"font-family: 宋体;\">2014.10.11</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0pt;\"><span lang=\"EN-US\"><span style=\"font-family: Times New Roman; font-size: small;\">&nbsp;</span></span></p>','0','1','1412994778');
