INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('38','275','material','溶栓降脂厚德纳豆，纳豆激酶降血脂，纳豆激酶厂家直销，纳豆批发,仅需150元+450积分','<p><img src=\"http://www.linglegou.com/data/files/store_275/goods_11/201406011156515228.jpg\" alt=\"2.jpg\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_26/201406011157068149.jpg\" alt=\"4.jpg\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_34/201406011157142243.jpg\" alt=\"5.jpg\" /></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 1.12em 0px; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><strong><span style=\"font-family: 宋体;\"><span style=\"font-size: 32px;\"><span style=\"font-size: 48px;\"><span style=\"font-family: kaiti_gb2312; color: #8c7301;\">厚德纳豆</span></span></span></span></strong></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\" align=\"center\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 32px;\"><span style=\"font-size: 48px;\"><span style=\"color: #8c7301; font-size: 24px;\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: #f10b00;\">&mdash;&mdash;&mdash;&mdash;<span style=\"font-family: kaiti_gb2312;\">心脑血管疾病的克星</span></span></strong></span></span></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"font-family: simsun; color: #8f0197; font-size: 32px;\">产品介绍：</span></strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">纳豆颗粒是由联合国国际生态安全科学院院士王厚德发明，由武汉世纪远荣生物科技有限责任公司开发的高科技保健食品。该产品是以</span><span><span style=\"font-family: \'times new roman\'; color: #000000; font-size: 24px;\">&ldquo;</span></span><span style=\"font-family: 宋体; color: #000000; font-size: 24px;\">王氏发酵技术</span><span><span style=\"font-family: \'times new roman\'; color: #000000; font-size: 24px;\">&rdquo;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">提取的高纯度<span style=\"color: #f10b00;\">纳豆菌</span>和<span style=\"color: #f10b00;\">纳豆激酶</span>为原料，通过添加其它天然高营养物，按照</span><span><span style=\"font-family: \'times new roman\'; color: #f10b00; font-size: 24px;\">&ldquo;</span></span><span style=\"font-family: 宋体; color: #f10b00; font-size: 24px;\">君臣佐使</span><span><span style=\"font-family: \'times new roman\'; color: #f10b00; font-size: 24px;\">&rdquo;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">的中药原理进行科学配伍而成，<span style=\"color: #f10b00;\"><span style=\"font-size: 32px;\">经临床证明</span>，对普通人群，亚健康人群，高血压、高血脂、脑血栓、脑梗塞、心肌梗塞、冠心病等心脑血管疾病患者、中风康复患者等有很好的效果，并且无任何副作用，<span style=\"color: #000000;\">是预防和改善心脑血管疾病的</span></span></span><span><span style=\"font-family: \'times new roman\'; color: #f10b00; font-size: 32px;\">&ldquo;</span></span><span style=\"font-family: 宋体; color: #f10b00; font-size: 32px;\">神奇食品</span><span><span style=\"font-family: \'times new roman\'; color: #f10b00; font-size: 32px;\">&rdquo;</span></span><span style=\"font-family: 宋体; color: #f10b00; font-size: 32px;\">。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong>【主要配料】：以富硒大豆、葛根、山楂提取物等为原料。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【主要成分】：<span style=\"color: #000000;\">纳豆激酶、纳豆菌、大豆多肽、卵磷脂、大豆异黄酮、红曲、葛根提取物、维生素</span></span><span><span style=\"font-family: \'times new roman\'; color: #000000; font-size: 24px;\">K2</span></span><span style=\"font-family: 宋体; color: #000000; font-size: 24px;\">等。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【功效成分及含量】：纳豆激酶</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">3200FU/</span></span><span style=\"font-family: 宋体; font-size: 24px;\">克，纳豆菌</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">300</span></span><span style=\"font-family: 宋体; font-size: 24px;\">亿</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">/</span></span><span style=\"font-family: 宋体; font-size: 24px;\">克</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【保健功能】：</span><span style=\"font-family: 宋体; font-size: 24px;\">预防心脑血管病</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong>【食用方法】：温水吞服，一日1次，一次1袋。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【规</span><span style=\"font-family: 宋体; font-size: 24px;\">格】</span><span style=\"font-family: tahoma, arial, 宋体, sans-serif;\"><span style=\"font-family: \'times new roman\'; font-size: 24px;\">: 7</span></span><span style=\"font-family: 宋体; font-size: 24px;\">克</span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">/</span><span style=\"font-family: \'times new roman\'; font-size: 24px;\"><span style=\"font-family: 宋体;\">袋，共30袋</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【贮藏方法】</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">:</span></span><span style=\"font-family: 宋体; font-size: 24px;\">密封、置阴凉干燥处</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【执行标准】：</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">Q/WYR 0002S-2012</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong>【生产日期】：见外包装盒</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">【保质</span><span style=\"font-family: 宋体; font-size: 24px;\">期】：</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">24</span></span><span style=\"font-family: 宋体; font-size: 24px;\">个月</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"color: #8f0197; font-size: 32px;\">产品成分：</span></strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"background-color: #feed9b; color: #000000;\">【纳豆激酶】</span>是直接分解血栓的纤维蛋白酶。具有强大的溶栓作用，而且安全、无副作用。食用纳豆后，血液中形成血栓的物质会慢慢减少，<span style=\"background-color: #013add;\"><span style=\"color: #013add;\"><span style=\"background-color: #ffffff; color: #0021b0;\">溶解血栓</span></span></span>的物质会越来越多，达到动态平衡。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"background-color: #feed9b;\">【大豆多肽】</span>也称大豆蛋白肽，具有高营养、易吸收、降血压、降胆固醇、促进脂质代谢、促进微生物生长、低抗原、调节血糖、提高耐力、促进矿物质吸收等多种生理功能。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"background-color: #feed9b; color: #000000;\">【卵磷脂】</span>被誉为与</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">&ldquo;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">蛋白质、维生素</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">&rdquo;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">并列的第三营养素，能分解沉积于动脉壁上的斑块，促进血管的弹性及增进血液循环。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"background-color: #feed9b; color: #000000;\">【大豆异黄酮】</span>有降低血脂、防止动脉硬化、<span style=\"color: #013add;\">抗衰老</span>作用。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"background-color: #feed9b; font-family: 宋体; color: #000000; font-size: 24px;\">【维生素</span><span><span style=\"background-color: #feed9b; font-family: \'times new roman\'; color: #000000; font-size: 24px;\">K2(Vitamin K2)</span></span><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"background-color: #feed9b; color: #000000;\">】</span>可预防肝硬化进展为肝癌，具有利尿、强化肝脏的解毒功能，并能降低血压。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"background-color: #feed9b; color: #000000;\">【葛根】</span>对高血压、高血脂、高血糖和心脑血管疾病有很好作用。葛根素能降低血糖，能明显降低血清胆固醇。同时对**，尤其是冠心病、骨质疏松症、妇女更年期综合症和老年性痴呆等有预防作用！</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 1.12em 0px; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\">&nbsp;</p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0pt 0px; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><span style=\"background-color: #ffffff; font-family: 宋体; color: #000000; font-size: 24px;\"><strong>【厚德物质】王厚德独秘配方，能</strong></span><span><strong>促使<span style=\"color: #013add;\">端粒酶</span>活性增强</strong></span><span><strong>，<span style=\"color: #013add;\">延缓衰老</span>，恢复青春状态之作用。</strong></span></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"color: #8f0197; font-size: 32px;\">&nbsp;</span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; color: #8f0197; font-size: 32px;\">纳豆胶囊</span><span style=\"font-family: 宋体; color: #8f0197; font-size: 32px;\">溶栓四步曲</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"background-color: #feed9b; color: #000000;\">【第一阶段：溶质除杂活血液】：</span>纳豆胶囊中的<span style=\"color: #f10b00; font-size: 32px;\">纳豆激酶</span>进入人体血管后，开始溶解血管中的杂质，使血液逐步活起来。此时，患者胸闷心慌、头晕眼花、失眠、手脚发麻僵硬等症状逐渐消失，血压、血脂开始降低。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"background-color: #feed9b;\">【第二阶段：溶栓通血降三高】：</span>纳豆胶囊服用一段时期后，吸收的纳豆激酶增多，使血栓逐渐溶解，血液开始循环通畅，避免了二次中风的危险</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">血压、血脂接近正常值、并且稳定</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">重病患者麻木的肢体，开始稍有知觉。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"background-color: #feed9b; color: #000000;\">【第三阶段：扩管供血滋神经】：</span>纳豆胶囊中的<span style=\"color: #000000;\">纳豆激酶</span>增强了血管弹性，使管腔逐渐增大，血液供应更加充分</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">脑部神经得到了有效滋润，侧肢循环更加密集发达</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">重病患者语言功能开始恢复，原来口齿不清的人，说话能成句了，眼睛有神，感觉肢体有力。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"background-color: #feed9b;\">【第四阶段：清血排脂回健康】：</span>纳豆胶囊使人体的全身血液得到了一次完全清洗，血栓及多余血脂、粥样斑块都被分解，排出体外</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span><span style=\"font-family: 宋体; font-size: 24px;\">动脉血管得到修复，重病患者的运动功能恢复明显。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"color: #8f0197; font-size: 32px;\">产品特点</span></strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><span style=\"background-color: #feed9b;\"><strong><span style=\"color: #0021b0;\">【一大特点】：纳豆权威，精心配制。</span></strong></span></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">&nbsp;&nbsp;&nbsp; 纳豆胶囊由世界权威生态科学家王厚德教授发明，精选数</span><span style=\"font-family: 宋体; font-size: 24px;\">十种珍贵原料科学配比而成。具有领先的技术优势。</span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><span style=\"background-color: #feed9b;\"><strong><span style=\"color: #0021b0;\">【二大特点】：溶、高、活、全。</span></strong></span></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">&nbsp;&nbsp;&nbsp;<span style=\"color: #f10b00;\">溶：</span>纳豆胶囊中富含高活性的<span style=\"color: #f10b00; font-size: 32px;\">纳豆激酶</span>，达到每克</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\"><span style=\"color: #f10b00; font-size: 32px;\">3200FU</span></span></span><span style=\"font-family: 宋体; font-size: 24px;\">以上，能够</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">快速</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">安全溶解血栓</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: #f10b00;\">高：</span>纳豆胶囊中所含的活性成分均是高纯度提纯，<span style=\"color: #fe2419; font-size: 32px;\">纳豆菌</span>含量每克</span><span><span style=\"font-family: \'times new roman\'; color: #f10b00; font-size: 32px;\">300</span></span><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"color: #f10b00; font-size: 32px;\">亿</span>以</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">上，是其它产品的</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">3</span></span><span style=\"font-family: 宋体; font-size: 24px;\">倍，功效倍增</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 24px;\"><strong>&nbsp;&nbsp;&nbsp;<span style=\"color: #f10b00;\">活：</span>纳豆胶囊中高纯度的纳豆菌能够保护纳豆激酶不被人体酸性环境所破</strong></span></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">坏，使其完全融解于人体，促进有效物质在体内被迅速激活</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong><span style=\"color: #f10b00;\">&nbsp;&nbsp;&nbsp; 全：</span>纳豆胶囊科学配伍，将纳豆中的有效成分保存完好，使其全面调节身体</strong></span></span><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><strong>各项生理机能。</strong></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 24px;\"><span style=\"background-color: #feed9b;\"><strong><span style=\"color: #0021b0;\">【三大特点】：快、长、清。</span></strong></span></span></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"color: #fe2419;\">&nbsp;&nbsp;&nbsp; 快：</span>纳豆胶囊中的纳豆物质能够快速作用于人体，比传统尿激酶快</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">16</span></span><span style=\"font-family: 宋体; font-size: 24px;\">倍</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\"><span style=\"color: #fe2419;\">&nbsp;&nbsp;&nbsp; 长：</span>纳豆胶囊中的纳豆激酶长时间作用于人体，时间高达</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">8</span></span><span style=\"font-family: 宋体; font-size: 24px;\">小时，不间断溶</span></strong></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">解血栓</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; padding: 0px;\"><span style=\"font-size: 16px;\"><span style=\"font-family: 宋体; font-size: 24px;\"><strong><span style=\"color: #fe2419;\">&nbsp;&nbsp;&nbsp; 清：</span>纳豆胶囊中的纳豆综合物质共同发挥作用，将多余有害物质不断清除出</strong></span></span><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 宋体; font-size: 24px;\">体内</span><span><span style=\"font-family: \'times new roman\'; font-size: 24px;\">;</span></span></strong></span></p>\r\n<p style=\"widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0cm 0cm 0pt; font: 14px/21px tahoma, arial, 宋体, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; w