INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('43','275','material','太岁典藏酒 肉灵芝酒 增强免疫 抗衰老 提升性肾功能','<p style=\"widows: 2; text-transform: none; text-indent: 0px; font: 12px/20px Verdana, Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\"><span style=\"font-family: Verdana; font-size: small;\">太岁典藏酒（<span style=\"font-family: 楷体_GB2312;\">新品</span>）</span></p>\r\n<p style=\"widows: 2; text-transform: none; text-indent: 0px; font: 12px/20px Verdana, Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\"><span style=\"font-family: Verdana;\">肉灵芝 &bull; 酒&nbsp; &asymp; 太岁典藏<br />太岁又称肉灵芝是自然界中，非植物、非动物和非菌类的第四种生命体。<br />史记说：秦始皇派徐福率3000童男童女东渡扶桑寻找人生不老药 &mdash; 肉灵芝。<br />本草说：肉芝状如肉，乃生物也。白者如截肪，黄者如紫金，皆光明洞,彻如坚冰也。久食，轻身不老，延年神仙。</span></p>\r\n<p style=\"widows: 2; text-transform: none; text-indent: 0px; font: 12px/20px Verdana, Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\"><span style=\"font-family: Verdana;\">&nbsp; 在中国酒文化的长河中，肉灵芝与酒的如影相随：道家组方、秘制炼丹之术，在特殊环境下，配以81味上品原料为组方、经过24道工序，精炼成&ldquo;肉灵芝母液&rdquo;，再以上乘美酒经特定醇化，酿造成绝品琼浆；品饮后，通经络、活气血、能迅速调节人体阴阳平衡。因数量有限，一直在道家秘传，外界知道甚少。<br />21世纪的今天，&ldquo;太岁典藏&rdquo;在继承先辈精华的基础上，运用古老的真传&ldquo;工艺&rdquo;与现代技术相融合；将古老的国宝&ldquo;肉灵芝&rdquo;规模化；使其功效更加完美，神奇之妙也，给力！</span></p>\r\n<p style=\"widows: 2; text-transform: none; text-indent: 0px; font: 12px/20px Verdana, Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\"><span style=\"font-family: Verdana;\">&ldquo;太岁典藏&rdquo;酒<br />&ldquo;太岁典藏&rdquo;精选优质纯粮酒为基酒，结合相配以清酒、黄精、枸杞子、蜂蜜、植物香料、浓缩果汁等等药食同源原料81味；在中国科学院、李时珍医道研究院等指导下，与现代生物工程技术融会贯通，运用世界独有生物活性提取技术，使酒香与&ldquo;肉灵芝母液&rdquo;独特气质完养融合、和谐天成。酿造出调理阴阳和谐的健康白酒&mdash;&mdash;太岁典藏。<br />该酒富含生命所必需的各种营养物物质；其色似宝石瑰丽，其状如九天甘露，唇初沾而馨绵，口将咽而生甜，温胃暖腰，回味无穷。长期饮用、延年益寿！<br />特点：<br />一、太岁典藏 &mdash;&mdash;&nbsp; 一种免疫增强剂<br />二、太岁典藏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 一种有效的抗衰老剂<br />三、太岁典藏&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 一种性肾功能提升剂<span>&nbsp;</span><br />健康&ldquo;白酒&rdquo;&not;&mdash;太岁典藏<br />中国人自古好饮酒，经过千百年的流转，那泓琼浆玉液已经渗透到华夏文明的各个领域。无数名门大家，在杯盏之间不断领略和传授以酒养生、以酒悟道的法则。<br />太岁典藏千年传承，以中医理论为依据，以天然药食草本为原料，古老&ldquo;工艺&rdquo;与现代生物技术相融合，经过24道工序精心制备而成。<br />她是一种新型多糖类的高级健康饮品，集白酒、红酒、药酒之综合优势，<br />从功能、口感、色泽等方面，满足人们追求时尚、优雅、健康之需求。<br />酌量品饮，补充体能，消除疲劳，补肾抗衰。</span></p>\r\n<p style=\"widows: 2; text-transform: none; text-indent: 0px; font: 12px/20px Verdana, Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\">&nbsp;</p>\r\n<p style=\"widows: 2; text-transform: none; text-indent: 0px; font: 12px/20px Verdana, Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: #333333; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\"><img src=\"http://www.linglegou.com/data/files/store_275/goods_71/201406011634312557.png\" alt=\"2.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_73/201406011634333621.png\" alt=\"3.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_75/201406011634358418.png\" alt=\"4.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_18/201406011756587089.png\" alt=\"5.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_19/201406011757008562.png\" alt=\"6.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_21/201406011757013077.png\" alt=\"7.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_22/201406011757024129.png\" alt=\"8.png\" /></p>','2598','保健品	保健食品	酒','太岁','0','','','1','0',null,'1401587874','1403223846','88','data/files/store_275/goods_162/small_201406011612428651.jpg','1','2595','2596','2598','0','150.00','630','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('45','275','material','肺和太岁 清肺养阴，润肺生津，止咳平喘，宣肺益气','<p><img src=\"http://www.linglegou.com/data/files/store_275/goods_161/201406021142415040.png\" alt=\"2.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_163/201406021142434268.png\" alt=\"3.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_165/201406021142454232.png\" alt=\"4.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_166/201406021142466980.png\" alt=\"5.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_168/201406021142485323.png\" alt=\"6.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_169/201406021142491145.png\" alt=\"7.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_171/201406021142516087.png\" alt=\"8.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_173/201406021142534590.png\" alt=\"9.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_177/201406021142578157.png\" alt=\"10.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_179/201406021142597516.png\" alt=\"11.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_181/201406021143019464.png\" alt=\"12.png\" /><span style=\"font-size: 14pt; font-family: 宋体;\">保护肺<span lang=\"EN-US\">&mdash;&mdash;</span>当&ldquo;</span><span style=\"font-size: 14pt; font-family: 宋体; color: #cc0000;\">雾</span><span style=\"font-size: 14pt; font-family: 宋体;\">&rdquo;之急&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\">当</span><span style=\"font-size: 14.0pt; font-family: 宋体; mso-ascii-font-family: Arial; color: #cc0000;\">雾霾</span><span style=\"font-size: 14pt; font-family: 宋体;\">成为经常和长期的污染时，我们身在其中、无法逃避的人们就要对肺重视起来了。因为，肺病会波及每个其中的人，肺癌已不是别人的事，也许不仅仅是对吸烟人士的威胁。随着肺癌的急剧上升，说明污染带来的危害可能比吸烟更可怕。看一组有关统计数据吧： </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> 2001</span><span style=\"font-size: 14pt; font-family: 宋体;\">至<span lang=\"EN-US\">2010</span>年，北京市肺癌发病率增长了<span lang=\"EN-US\">56</span>％。<span lang=\"EN-US\">35</span>岁以上人群的肺癌发病率上升加速。纵观全国，肺癌的发病率和死亡率也在成百倍的增长，卫生部提供的第三次全国人口死因调查数字显示，全国肺癌死亡率在过去<span lang=\"EN-US\">30</span>年增加了<span lang=\"EN-US\">465</span>％。 </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> &nbsp;&nbsp;&nbsp; <br /> </span><span style=\"font-size: 14pt; font-family: 宋体;\">世界卫生组织频频发出警告：肺癌是一种发病率特别高、威胁特别大、死亡率特别高的难治性癌症，应当引起全社会的高度重视！ </span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\" lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\">呼吸道系统疾病的形成 </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> 1</span><span style=\"font-size: 14pt; font-family: 宋体;\">、正气不足，而后邪气袭之。因为年老带来内虚，因为长期吸烟耗损阴精，因为长期吸入有毒有害气体，羁留肺窍，阻塞气道，最后导致湿瘀血凝结形成瘤。 </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> 2</span><span style=\"font-size: 14pt; font-family: 宋体;\">、邪毒侵肺。包括家居、空气的放射性物质、有毒的化学物质，致使肺气瘀滞不宣，留于肺脏，毒聚斜留，肿块逐渐形成。 </span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> </span><span style=\"font-size: 14pt; font-family: 宋体;\">我们开始了当<span lang=\"EN-US\">&ldquo;</span></span><span style=\"font-size: 14.0pt; font-family: 宋体; color: red;\">雾</span><span style=\"font-size: 14pt; font-family: 宋体;\" lang=\"EN-US\">&rdquo;</span><span style=\"font-size: 14pt; font-family: 宋体;\">之急的研究，因为，呼吸才能活着，可呼吸如果影响了活着，我们必须采取行动自卫，除了你不要吸烟，不要使用有毒有害的装修物，你能做的就是提高肺的正能量，能驱赶邪气，能抵御毒物，能宣泄废物。这三点我们能帮您做到。科研人员为百姓设计了独特的</span><span style=