INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('104','275','material','肺和太岁 清肺养阴，润肺生津，止咳平喘，宣肺益气','<p><img src=\"http://www.linglegou.com/data/files/store_275/goods_161/201406021142415040.png\" alt=\"2.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_163/201406021142434268.png\" alt=\"3.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_165/201406021142454232.png\" alt=\"4.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_166/201406021142466980.png\" alt=\"5.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_168/201406021142485323.png\" alt=\"6.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_169/201406021142491145.png\" alt=\"7.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_171/201406021142516087.png\" alt=\"8.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_173/201406021142534590.png\" alt=\"9.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_177/201406021142578157.png\" alt=\"10.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_179/201406021142597516.png\" alt=\"11.png\" /><img src=\"http://www.linglegou.com/data/files/store_275/goods_181/201406021143019464.png\" alt=\"12.png\" /><span style=\"font-size: 14pt; font-family: 宋体;\">保护肺<span lang=\"EN-US\">&mdash;&mdash;</span>当&ldquo;</span><span style=\"font-size: 14pt; font-family: 宋体; color: #cc0000;\">雾</span><span style=\"font-size: 14pt; font-family: 宋体;\">&rdquo;之急&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\">当</span><span style=\"font-size: 14.0pt; font-family: 宋体; mso-ascii-font-family: Arial; color: #cc0000;\">雾霾</span><span style=\"font-size: 14pt; font-family: 宋体;\">成为经常和长期的污染时，我们身在其中、无法逃避的人们就要对肺重视起来了。因为，肺病会波及每个其中的人，肺癌已不是别人的事，也许不仅仅是对吸烟人士的威胁。随着肺癌的急剧上升，说明污染带来的危害可能比吸烟更可怕。看一组有关统计数据吧： </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> 2001</span><span style=\"font-size: 14pt; font-family: 宋体;\">至<span lang=\"EN-US\">2010</span>年，北京市肺癌发病率增长了<span lang=\"EN-US\">56</span>％。<span lang=\"EN-US\">35</span>岁以上人群的肺癌发病率上升加速。纵观全国，肺癌的发病率和死亡率也在成百倍的增长，卫生部提供的第三次全国人口死因调查数字显示，全国肺癌死亡率在过去<span lang=\"EN-US\">30</span>年增加了<span lang=\"EN-US\">465</span>％。 </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> &nbsp;&nbsp;&nbsp; <br /> </span><span style=\"font-size: 14pt; font-family: 宋体;\">世界卫生组织频频发出警告：肺癌是一种发病率特别高、威胁特别大、死亡率特别高的难治性癌症，应当引起全社会的高度重视！ </span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\" lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\">呼吸道系统疾病的形成 </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> 1</span><span style=\"font-size: 14pt; font-family: 宋体;\">、正气不足，而后邪气袭之。因为年老带来内虚，因为长期吸烟耗损阴精，因为长期吸入有毒有害气体，羁留肺窍，阻塞气道，最后导致湿瘀血凝结形成瘤。 </span><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> 2</span><span style=\"font-size: 14pt; font-family: 宋体;\">、邪毒侵肺。包括家居、空气的放射性物质、有毒的化学物质，致使肺气瘀滞不宣，留于肺脏，毒聚斜留，肿块逐渐形成。 </span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> </span><span style=\"font-size: 14pt; font-family: 宋体;\">我们开始了当<span lang=\"EN-US\">&ldquo;</span></span><span style=\"font-size: 14.0pt; font-family: 宋体; color: red;\">雾</span><span style=\"font-size: 14pt; font-family: 宋体;\" lang=\"EN-US\">&rdquo;</span><span style=\"font-size: 14pt; font-family: 宋体;\">之急的研究，因为，呼吸才能活着，可呼吸如果影响了活着，我们必须采取行动自卫，除了你不要吸烟，不要使用有毒有害的装修物，你能做的就是提高肺的正能量，能驱赶邪气，能抵御毒物，能宣泄废物。这三点我们能帮您做到。科研人员为百姓设计了独特的</span><span style=\"font-size: 14.0pt; font-family: 宋体; color: #333333;\">以太岁母液和清肺养肺的组方二次发酵。肺为相傅之官，司气血分布。呼吸吐纳有度，身体的细胞、组织、器官不会因为血氧浓度不够而萎缩、衰绝。短期调理肺部，清肺养肺，止咳化痰平喘，对于身体平衡有重要意义。</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14pt; font-family: 宋体;\" lang=\"EN-US\">&nbsp;</span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 14.0pt; font-family: 宋体;\" lang=\"EN-US\"><br /> </span><span style=\"font-size: 14.0pt; font-family: 宋体; color: #333333;\">太岁肺和口服液经反复研究实验，精选药食同源的清肺止咳平喘之名贵中药材：灵芝孢子粉、虫草菌丝体、石斛、雪蛤、紫苏子等，利用道家太岁生物发酵技术，清肺养阴，润肺生津，止咳平喘，宣肺益气。各种久咳不愈，慢性哮喘及各类慢性肺部疾病</span><span style=\"font-size: 14pt; font-family: 宋体;\">久咳痰多，专为提高肺功能而研制<span lang=\"EN-US\">,</span></span><span style=\"font-size: 14.0pt; font-family: 宋体; color: #333333;\">长期服用尤佳。常年服用，无任何毒副作用。</span></p>','2617','保健品	保健功效	增强免疫力','太岁','0','','','1','0',null,'1402263951','1404255379','154','data/files/store_275/goods_138/small_201406091345388796.png','1','2595','2616','2617','0','600.00','0','');
