INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('164','79','material','沛缇优酵365饮液,益生源饮液,排宿便清肠胃','<p>&nbsp;</p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\"><span style=\"color: #ff99cc;\">容量：</span>10mlX30瓶/盒&nbsp;<span style=\"color: #ff99cc;\">备注：</span>克丽缇娜直销产品</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px; color: #ff99cc;\">沛缇优酵365饮液产品说明：</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">1、帮助人体更容易吸收食物中养分，以自然的方式维持肠道菌丛平衡，可以净化、顺畅、轻体、减少疲劳感、延长青春保鲜期。<br />2、大豆、薏仁、芝麻珍萃可迅速被人体吸收利用，是全家人或健康亮红灯者必备的营养补给液。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">三大独特营养素&nbsp;&nbsp;<br />A、大豆提取物：体内环保、平衡轻盈<br />B、薏仁提取物：畅通循环、顺心有元气<br />C、芝麻提取物：青春保鲜零负担</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px; color: #ff99cc;\">沛缇优酵365饮液适宜人群：</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">优酵365为天然发酵食品，制程中不添加任何化学物质、香料、色素或防腐剂等人工添加剂，100%天然，安全无副作用，使用后不会对肝、肾造成负担，大人、小孩、孕妇、婴幼儿皆可饮用，尤其是三餐不正常、经常外食、每天都会吃到肉类、不吃早餐、很少运动，35岁以上的青壮年、工作压力大者更为适合。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px; color: #ff99cc;\">沛缇优酵365饮液产品组成：</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">大豆提取物、薏仁提取物、芝麻提取物、水。<br />&nbsp;含有氨基酸、植物皂苷、木酚素等珍贵的营养元素，对人体大有益处。耐胃酸、耐胆碱、可于无氧环境中生存。可帮助肠道抵御坏菌,增值好菌，加速毒物排出。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px; color: #ff99cc;\">沛缇优酵365饮液使用方法：</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">每日食用2-3次，每次1瓶（10ml），根据个人喜好，使用专属吸管插入瓶中直接饮用，或依个人口味将其加约100cc饮用水稀释，空腹食用效果更佳。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px; color: #ff99cc;\">沛缇优酵365饮液保存方法：</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">置于阴凉干燥处，避免高温及阳光直射。本品100%无添加任何防腐剂，为保证产品鲜度，开瓶后请尽速食用完毕。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px; color: #ff99cc;\">沛缇优酵365饮液注意事项：</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\">本品皆为纯天然谷物发酵熟成，若有沉淀物属正常现象，请安心使用（饮用前请稍加摇晃，有助于珍贵成分吸收）。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\"><img src=\"http://img1.tcgimg.com/Uploadfiles/bbs/2013/8/22/635127719308936564.jpg?f=1306449\" alt=\"\" width=\"551\" height=\"800\" /></span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-size: 16px;\"><img src=\"http://img1.tcgimg.com/Uploadfiles/bbs/2013/8/22/635127720661314934.jpg?f=1306453\" alt=\"\" width=\"627\" height=\"800\" /><br /></span></p>\r\n<p>&nbsp;</p>','2606','保健品	保健食品	口服液','沛缇','0','','','1','0',null,'1402450982','1404936990','219','data/files/store_79/goods_79/small_201406111741197578.jpg','1','2595','2596','2606','0','0.00','798',',沛缇优酵365饮液,益生源饮液,排宿便清肠胃,');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('165','79','material','克丽缇娜正品沛缇多效赋活饮美白美颜饮胶原蛋白去皱祛斑','<p>&nbsp;</p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\"><span style=\"font-family: arial; color: #000000; line-height: 22px;\">欧洲 美国 日本 韩国的多项专利保证 一周感受奢华亮丽 三重多效赋活 六大赋活因子。&nbsp;</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">第一重，白皙晶亮 气色红润</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">谷胱甘肽</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">瑞士研发的酵母活性物质，纯度高达90%，通过美国PDA认证，具有欧州及美国的专利。通过舌下能迅速帮助人体肠胃吸收，有效挥别黯沉 蜡黄无光泽的肌肤，提升肌肤亮白度，让肌肤白皙净亮，散发动人的明亮光彩。</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">31种蔬果精萃</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">精选31种天然蔬果及莓类萃取物，包含山桑子 野樱桃 西印度樱桃 覆盆莓 巴西紫莓 蓝莓 黑莓 美国葡萄籽 山竹果 苹果 蕃茄 胡萝卜 花椰菜 绿茶 黑豆 姜黄 蓝玉米等精华，抵抗生活压力与补充现代人精致饮食所造成的营养缺乏，均衡营养，展现自然 健康 美丽。</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">第2重Q弹紧致 水嫩保湿</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">燕窝酸</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">从牛乳中萃取的高浓度燕窝酸，可减低红血球细胞被分解的速度，从而延缓因老化引起的衰老现象，来源安全，不含荷尔蒙。</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">第三重年轻赋活 强化再生</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">日本专制GABA专利许可，可补充人体的GABA元素，可镇静，修护饱受外部压力的问题肌肤，同时也舒缓身心疲惫，重拾年轻活力。</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">爱尔兰红藻</span><br style=\"line-height: 22px; font-family: arial; color: #000000;\" /><span style=\"font-family: arial; color: #000000; line-height: 22px;\">爱尔兰红藻具欧盟无毒认证，富含超过70种自然均衡的矿物质与微量元素，能与GABA产生相互协同作用，补绐美丽肌肤所需的营养素。</span></p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\">注意事项：</p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\">多效赋活饮含有31种蔬果精华，其沉淀物正是植物精华所在，建议您摇匀后再饮用，让精华的已完全吸收。</p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\">多效赋活饮每瓶钠含量1.5mg/每日限量2，400mg,糖含量4g每日限22g，皆低于每日限量，糖尿病与肾脏病患者皆可安心食用。饮用方式则建议改于饭后或随餐饮用即可。</p>\r\n<p style=\"word-break: break-all; line-height: 30px; color: #666666; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\">多效赋活饮所添加的成分皆属天然且安全无虞，一般人皆可饮用，但孕妇、哺乳妇女及婴幼儿（6岁以下）建议由三餐中摄取营养补充即可。</p>\r\n<p>&nbsp;</p>','2840','保健品	保健品牌	克丽缇娜','克缇','0','','','1','0',null,'1402451154','1404937013','220','data/files/store_79/goods_97/small_201406111744571738.jpg','1','2595','2644','2840','0','0.00','688',',克丽缇娜正品沛缇多效赋活饮美白美颜饮胶原蛋白去皱祛斑,');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('166','79','material','沛缇鲑蒜软胶囊热销保健品克缇高血压降血脂预防三高','<p>&nbsp;</p>\r\n<div id=\"pr-ds\" style=\"word-break: break-all; margin: 1px 0px 0px; padding: 3px 5px; clear: both; border: 1px solid #dddddd; line-height: 30px; color: #666666; overflow: hidden; font-family: 宋体, Arial, Helvetica, clean, sans-serif; text-align: left;\">\r\n<div style=\"word-break: break-all; margin: 0px; padding: 0px; overflow-x: hidden;\">\r\n<table style=\"border-collapse: collapse; border-spacing: 0px; font-size: 12px;\" border=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td style=\"word-break: break-all; line-height: 30px;\">\r\n<p style=\"word-break: break-all;\">中文名：鲑蒜软胶囊<br />英文名：Salomon Oil Plus<br />零售价：RMB580.00<br />净含量：46.8g（0.52g&times;90粒/盒）</p>\r\n<p style=\"word-break: break-all;\">健康养护　辅助降血脂</p>\r\n<p style=\"word-break: break-all;\">建议食用方法：每日2次，每次6粒<br />&nbsp;<br />使用方法：<br />产品成份：鲑鱼油、维生素E、绿茶提取物、菜蓟提取物、大蒜提取物、蜂蜡、单硬脂酸甘油酯、甘油、明胶、纯化水、二氧化钛、胭脂红、日落黄、亮蓝<br />产品介绍：<br />&nbsp;　　提取深海中鲑鱼及多种营养成分，帮助调节身体循环内环境，调节血压和血脂，保护心脑血管健康。</p>\r\n<p style=\"word-break: break-all;\">&nbsp;</p>\r\n<p style=\"word-break: break-all;\">关于鲑蒜软胶囊的更多知识</p>\r\n<p style=\"word-break: break-all;\">鲑鱼油<br /> 鲑鱼油中含约30%人体中无法制造的长链的多元不饱和omega-3必需脂肪酸（EPA/DHA），补充人体日常饮食中的缺少营养，促进身体的健康循环。</p>\r\n<p style=\"word-break: break-all;\">大蒜<br /> 大蒜的味道及功效都是来自它的含硫化合物，这些含硫的物质可以预防血液中的有害物质产生，维护身体健康。</p>\r\n<p style=\"word-break: break-all;\">绿茶<br /> 含有益身体健康的植物性化合物&mdash;茶多元酚（儿茶素类），能提升高密度脂蛋白胆固醇含量，帮助平衡血压。</p>\r\n<p style=\"word-break: break-all;\">朝鲜蓟<br /> 朝鲜蓟是古老的药草，却有神奇的效果，16世纪时它已是欧洲贵族最喜爱的食物之一，能够帮助肝脏分泌胆汁分解脂肪，调节血脂健康。</p>\r\n<p style=\"word-break: break-all;\">健康防&ldquo;脂&rdquo;很重要</p>\r\n<p style=\"word-break: break-all;\">现代人的生活工作繁忙，日常饮食中的营养含量不断上升，导致过多的脂类堆积在体内，用不完排不掉，日积月累胆固醇、甘油三酯逐渐升高，出现记忆力减退、困倦无神、身体发胖、容易疲劳等症状，因此在日常生活中应注意膳食均衡并提早预防。</p>\r\n<p style=\"word-break: break-all;\"><img src=\"http://img1.tcgimg.com/Uploadfiles/bbs/2013/8/22/635127764939332033.jpg?f=1306504\" alt=\"\" width=\"730\" /></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n<div id=\"pay-help\" style=\"word-break: break-all; margin: 0px; padding: 0px; width: 743px; color: #333333; font-family: 宋体, Arial, Helvetica, clean, sans-serif; line-height: 19px; text-align: left;\">\r\n<div id=\"help-dl\" style=\"word-break: break-all; margin: 0px; padding: 10px 0px;\"><dl style=\"word-break: break-all; width: 180px; float: left;\"><br /></dl></div>\r\n</div>\r\n<p>&nbsp;</p>','2840','保健品	保健品牌	克丽缇娜','沛缇','0','','','1','0',null,'1402451387','1404937033','221','data/files/store_79/goods_159/small_201406111749199757.jpg','1','2595','2644','2840','0','0.00','598',',沛缇鲑蒜软胶囊热销保健品克缇高血压降血脂预防三高,');
