INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('291','128','material','维骨康咀嚼片','<table border=\"0\" cellspacing=\"2\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td style=\"font-size: 14px;\"><strong>中文名：维骨康咀嚼片 </strong></td>\r\n</tr>\r\n<tr>\r\n<td style=\"font-size: 14px;\"><strong>英文名：MailGood-Ca</strong></td>\r\n</tr>\r\n<tr>\r\n<td><strong>零售价</strong>：RMB350.00</td>\r\n</tr>\r\n<tr>\r\n<td><strong>净含量</strong>：75g（0.135g&times;550片/瓶）</td>\r\n</tr>\r\n<tr>\r\n<td><strong>编号</strong>：50005</td>\r\n</tr>\r\n<tr>\r\n<td><strong>适用人群</strong>：特别推荐中老年人钙质流失补充</td>\r\n</tr>\r\n<tr>\r\n<td><strong>使用方法</strong>：建议每日2次，每次10粒，咀嚼食用。</td>\r\n</tr>\r\n<tr>\r\n<td><strong>产品成份</strong>：牡蛎、珍珠、蛋壳粉、酪蛋白磷酸肽(CPP)、维生素D3、乳糖、蔗糖脂肪酸酯、柠檬酸、杏仁香精、黄原胶</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"8\" cellpadding=\"0\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td id=\"text\" style=\"line-height: 35px;\"><strong>产品介绍</strong>：\r\n<p>　　多种天然钙源，补充身体流失钙，促进钙吸收，预防驼背及骨质疏松等。</p>\r\n<p><strong>　　关于补钙的更多知识</strong></p>\r\n<p>　　酪蛋白磷酸胜肽（CPP）<br />　　以牛奶之酪蛋白为主要原料，经胰酵素分解游离出来再经脱苦味酵素处理而成，CPP可抑制不溶性磷酸钙的形成，明显提高钙质的吸收效率。</p>\r\n<p>　　压力人群急需补钙<br />　　工作、生活的繁忙，压力不断积蓄，大量的钙质被消耗。钙具有抑制神经兴奋和不安，促进情绪稳定的作用。女性由于生理特性更易流失钙质，贮存钙质有助于预防骨质疏松。</p>\r\n<p>　　牡蛎钙：<br />　　取自严格挑选的新鲜优质牡蛎壳，以最新设备灭菌处理的天然钙源。主要成份为碳酸钙，其含钙量38％以上。</p>\r\n<p>　　蛋壳钙：<br />　　取自鸡卵壳部分，经洗净、杀菌、干燥、微粉碎而得的天然钙源。主要成份为碳酸钙，其含钙量38％以上。</p>\r\n<p>　　珍珠钙：<br />　　取自珍珠，经研磨洗净、粉碎而得的天然钙源。主要成份为碳酸钙，其含钙量约38％。此外珍珠还含有18种氨基酸、近20种微量元素、天然牛黄酸以及矿物质等营养元素。</p>\r\n<p>　　维生素D3：<br />　　活性维生素D3可促进肠道钙吸收，肾功能不健康和消化吸收能力差的老人，也能吸收利用，促进骨骼健康。</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>','2622','保健品	保健功效	增加骨密度','克缇-沛缇','0','','','1','0',null,'1403489498','1403489498','361','data/files/store_128/goods_156/small_201406231809163631.jpg','1','2595','2616','2622','0','258.00','0',',补钙,');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('292','67','material','思才教育卡 涵盖0-100岁的所有知识,纯积分，仅需300分','<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">思才国际教育机构成立于2006年，是由香港教育专家集合国际先进的教学经验，创立的规模化、专业化、品牌化的教育研究机构，主要服务于世界各地区汉语人士，亦是国际友人学习中华传统文化的畅通桥梁。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">思才国际教育机构旗下的深圳思才文化发展有限公司设立在大陆地区的办事机构，首创为中国家庭全员服务的\"网上学校\"，以最受喜闻乐见、最易学习吸收的视频（影音动漫）为主要载体，致力于打造中国家庭教育视频第一品牌，有效促进中国家庭乃至社会的健康和谐发展。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">家庭教育，是三大教育的最重要组成部分，是学校教育与社会教育的基础、补充和延伸。在转型期的当代中国，成年人的价值观发生巨变，家庭教育的支柱正在崩塌，重智轻德、亲情淡漠、沟通困难成为家庭教育的普遍趋向与焦点问题。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">思才家教网粹集中华传统国学的学习与应用、双语趣味学习、婴幼护理、亲情培养、幼儿早教智力开发、小/初/高学生课程同步教学、家长进修等精华教学内容，是值得中国每一位家庭成员（尤其家长）放心信任的绿色网站；通过观看学习，能够成就孩子德、智、体、美、劳均衡发展，且能促进全家亲情交融、共同进步。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">思才国际教育机构，始终坚持以人为本、与时俱进，成功解决和弥补传统教学的不足及缺失，并不断加强与各地知名院校/专家、成功机构/人士的合作，持续提升家庭教育的深度、广度及效益。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">思才国际教育机构依据卓越的行业经验、丰富的专家与材料资源，凭借互联网的强大推广优势等先进手段，兼收并蓄、融合弘扬中华传统与西方现代文化精华，必将实现多种类、多形式、多专业的教育方向延伸，成为现代教育体系的有力组成部分。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\">深圳思才文化发展有限公司是隶属于思才国际教育机构在大陆地区的主要以教育为主的机构，主要从事教育工作外，还在国家规定的经营范围内开展以下经营活动：文化交流活动策划、礼仪策划、展览展示策划；信息咨询（不含人才中介服务和其它限制项目）；计算机软硬件的技术开发，文化体育用品、电子产品的购销，国内贸易（不含专营、专控、专卖商品）；经营电子商务（法律、行政法规、国务院决定禁止的项目除外，限制的项目须取得许可后方可经营）。</p>\r\n<p style=\"line-height: 24px; font-family: Simsun; color: #666666; font-size: 14px;\" align=\"left\"><img src=\"http://www.linglegou.com/data/files/store_67/goods_24/201406241107046174.png\" alt=\"201406131421303883.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_26/201406241107068994.png\" alt=\"201406131420377168.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_29/201406241107097693.png\" alt=\"201406131420528534.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_31/201406241107113976.png\" alt=\"201406131421071640.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_33/201406241107139871.png\" alt=\"201406131421188984.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_59/201406241107395241.png\" alt=\"201406131429518469.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_60/201406241107405979.png\" alt=\"201406131429402033.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_61/201406241107412939.png\" alt=\"201406131429432912.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_62/201406241107429588.png\" alt=\"201406131429457835.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_63/201406241107435701.png\" alt=\"201406131429469170.png\" /><img src=\"http://www.linglegou.com/data/files/store_67/goods_64/201406241107446638.png\" alt=\"201406131429492939.png\" /></p>','2751','文化玩具	科教办公、学生用品	其他','思才教育','0','','','1','0',null,'1403550216','1403550668','362','data/files/store_67/goods_163/small_201406241102431465.jpg','1','2719','2744','2751','0','0.00','300','');
