INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('295','358','material','火棘果珍润滋养套装','<p>&nbsp;</p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\"><span style=\"font-family: kaiti_gb2312;\"><span style=\"font-family: simhei;\">火棘果</span>是一种蔷薇科植物，又称&ldquo;赤阳子&rdquo;、&ldquo;救兵粮&rdquo;，主要生长在中国大陆西北部高原地区。火棘果含有丰富的有机酸、蛋白质、氨基酸、维生素和多种矿物质元素，可鲜食，也可加工成各种产品。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 经临床实验证明，火棘果具有神奇的美白效果，可以抑制&ldquo;组胺&rdquo;刺激色素母细胞产生过多黑色素，具有淡化麦拉宁色素和保湿的神奇功效。同时，火棘果中富含的芦丁和金丝桃苷等黄酮类化合物能明显延缓荧光类物质被活性氧自由基萃灭，有很强的抗氧化效果和抗激变作用。传说，杨贵妃就是常年使用火棘果美白，才能&ldquo;温泉水滑洗凝脂&rdquo;！<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 如今，我公司将火棘果精华与瑞倪维儿独有的水通道蛋白技术相结合，在美白肌肤的同时将补水保湿效果发挥到了极致，营造独特的水润调节机制，充分滋润肌肤，梳理角质排列，清洁毛孔堆积杂质，令肌肤触感更细滑柔嫩。在调理肌肤角质并去除杂质的同时，更有效地带领美白成分迅速渗入肌肤内部，效果充分释放，净化黑色素角质，加快沉淀色素排泄，通透肌肤，由表及里地通畅肌肤养分通道，帮助肌肤进一步吸收美白营养成分，实现晶莹剔透的白。</span></span></span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\"><span style=\"font-family: simhei;\"><strong>火棘果珍润洗面奶 90g</strong></span><br />统一零售价：220元/瓶<br /><strong>产品成分：</strong><br />水、椰油酰甘氨酸钾、甘油、月桂醇磺基琥珀酸酯二钠、辛酰/癸酰氨丙基甜菜碱、月桂醇聚醚硫酸酯钠、椰油酰两性基乙酸钠甘油、聚乙二醇-120 甲基葡萄糖三异硬脂酸酯、甘油聚丙烯酸酯、PVM/MA 共聚物、甜菜碱、乙二醇二硬脂酸酯、火棘（PYRACANTHA FORTUNEANA）果提取物、PEG-17聚二甲基硅氧烷、柠檬酸、EDTA 二钠、氯化钠、丙二醇（和）双（羟甲基）咪唑烷基脲（和）碘丙炔醇丁基氨甲酸酯、霍霍巴酯类、红30色淀、香精。<br /><strong>使用功效：</strong><br />选用天然氨基酸洁肤因子，膏体呈弱酸性，对皮肤无刺激和伤害，乳白色外观，丰厚柔软的泡沫，有超强的洁面效果，内含霍霍巴柔珠颗粒，轻柔细腻，能有效清洁皮肤深层污垢和油脂；易冲洗，洁面后皮肤洁净、柔软、细腻、不紧绷。<br /><strong>使用方法：</strong><br />打湿双手，将火棘果珍润洗面奶取适量倒于掌心并揉出泡沫，以打圈方式在全脸按摩，然后用温水洗净。每天早晚各使用一次。</span></span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\"><span style=\"font-family: simhei;\"><strong>火棘果珍润养肤水 120ml</strong></span><br />统一零售价：280元/瓶<br /><strong>产品成分：</strong><br />水、丁二醇、丙二醇、 甘油聚丙烯酸酯、&nbsp; PVM/MA 共聚物、乙基己基甘油葡聚糖、火棘（PYRACANTHA FORTUNEANA）果提取物、葡聚糖、啤酒酵母菌（SACCHAROMYCES CEREVISIAE）提取物、乙基己基甘油、聚谷氨酸钠、苯氧乙醇、羟苯甲酯、三乙醇胺、EDTA 二钠、透明质酸、蓝1（CI42090）、胭脂红（CI 75470)、透明质酸、香精。<br /><strong>使用功效：</strong><br />有效控制细胞水分，并能维持细胞水分动态平衡，修正肤色，增进皮肤的弹性，提高皮肤自身的修护能力，铺展性极好，易吸收，不粘腻。涂后面部滑爽，舒适。<br /><strong>使用方法：</strong><br />早晚洁面后，取适量于手心，轻轻拍打于面部，使之融入肌肤。 感觉肌肤特别干燥时，可以反复涂抹。</span></span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\"><span style=\"font-family: simhei;\"><strong>火棘果珍润眼部精华 15ml</strong></span><br />统一零售价：500元/支<br /><strong>产品成分：</strong><br />水、甘油聚丙烯酸酯、甘油聚醚-26 、PVM/MA 共聚物、乙基己基甘油葡聚糖、甘油、丙二醇、聚甲基硅倍半氧烷、火棘（PYRACANTHA FORTUNEANA）果提取物、藻提取物、啤酒酵母菌（SACCHAROMYCES CEREVISIAE）提取物、葡聚糖、夏枯草（PRUNELLA VULGARIS）叶提取物、金纽扣（SPILANTES ACMELLA）花提取物、乙基己基甘油、丙烯酸钠/丙烯酰基二甲基牛磺酸钠共聚物/异十六烷/聚山梨醇酯-80、聚谷氨酸钠、苯氧乙醇、羟苯甲酯、EDTA 二钠、透明质酸、蓝1（CI42090）、胭脂红（CI 75470）、香精。<br /><strong>使用功效：</strong><br />超浓缩外观和涂铺感，配合超微波振荡，使有效成分迅速被眼周细胞吸收。用后，眼周皮肤有明显收紧感，淡化皱纹，平复细纹，减轻黑眼圈、眼袋，增加皮肤水分，平滑肌肤活性，有效改善皮肤的密度和紧实度，迅速提升整个眼部轮廓。<br /><strong>使用方法：</strong><br />使用前请将透明胶布从开关处揭开。每日早晚在洁肤、润肤后使用，旋拧下半部分使其和震头分离，用金属滚珠将精华涂抹于眼部肌肤（眼角及上、下眼睑），并用微震头均匀振动直至精华被彻底吸收。</span></span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\"><span style=\"font-family: simhei;\"><strong>火棘果珍润面霜 50g</strong></span><br />统一零售价：400元/瓶<br /><strong>产品成分：</strong><br />水、丙二醇、甘油、鲸蜡硬脂醇橄榄油酸酯、山梨坦橄榄油酯、霍霍巴（Simmondsia&nbsp; Chinensis）籽油、异壬酸异壬酯、聚二甲基硅氧烷、环戊硅氧烷、聚硅氧烷-11、异十六烷、聚丙烯酰基二甲基牛磺酸铵、聚山梨酯-20、聚山梨酯-80、牛油果树（BUTYROSPERMUM PARKII）坚果提取物、甘油硬脂酸酯、火棘（PYRACANTHA FORTUNEANA）果提取物、海藻糖、葡聚糖、烟酰胺、鲸蜡硬脂醇、1-甲基乙内酰胺-2-酰亚胺、啤酒酵母菌（SACCHAROMYCES CEREVISIAE）提取物、苯氧乙醇、丙烯酸钠/丙烯酰基二甲基牛磺酸钠共聚物/异十六烷/聚山梨醇酯-80、乙基己基甘油、生育酚乙酸酯、羟苯甲酯、羟苯丙酯、聚谷氨酸钠、蓝1（CI42090）、胭脂红（CI 75470）、香精。<br /><strong>使用功效：</strong><br />选用天然橄榄油乳化体，安全无刺激，外观细腻、有柔厚的丝滑质感；内含天然保湿因子（NMF），使皮肤更具润泽度和透明感；天然氨基酸衍生物，可以调节细胞生长和转化，提高皮肤色调的透明度和均匀性；内含组织呼吸因子，可以促进肌肤细胞的含氧量，使得皮肤更加饱满，增强紧致度，同时能修复受损肌肤；火棘果精华与天然水通道蛋白系统相配合，在美白、提亮肤色的同时，给面部深层的滋润，使皮肤更有弹性、更紧实、更细腻，并能长效保湿一整天。<br /><strong>使用方法：</strong><br />取本品均匀涂抹于面部轻拍至完全吸收。</span></span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\"><span style=\"font-family: simhei;\"><strong>火棘果珍润隔离霜 30g</strong></span><br />统一零售价：400元/瓶<br /><strong>产品成分：</strong><br />水、丙二醇、甘油、丁二醇、碳酸二辛酯、甲氧基肉桂酸辛酯、聚甘油基-2二聚羟基硬脂酸酯、辛酸/癸酸甘油三酯、二氧化钛 （CI 77891）、棕榈酸异丙酯、氧化锌、硬脂酸镁、蜂蜡、聚甘油基-3二异硬脂酸酯、聚二甲基硅氧烷、硫酸镁、海藻糖、火棘（PYRACANTHA FORTUNEANA）果提取物、红没药醇、云母、氯氧化铋（CI77163）、氧化铁（CI 77492）、氧化铁（CI 77491）、双（羟甲基）咪唑烷基脲、碘丙炔醇丁基氨甲酸酯、羟苯甲酯、羟苯丙酯、丁羟甲苯、透明质酸、香精。<br /><strong>使用功效：</strong><br />本品轻柔、细腻；具有超强的滋润保湿感；内含防辐射粒子，可有效抵御外界污染和光辐射对皮肤的伤害；具有特殊的柔焦效果，修正肤色，改善暗淡发黄等肌肤问题；使用后皮肤通透自然、白里透红，发出健康、诱人的光泽。<br /><strong>使用方法：</strong><br />在肌肤基础护理的最后一个步骤使用，用手指取适量隔离霜，轻轻压在两颊、额头、鼻子和下巴处，然后慢慢地推展开至整个面部。</span></span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px; color: #4a4a4a; list-style-type: none;\"><span style=\"font-size: 24px;\"><span style=\"color: #4c1130;\">套盒统一零售价：1800元/套</span></span></p>\r\n<p style=\"color: #666666; font-family: tahoma, \'Hiragino Sans GB\', \'microsoft yahei\', sans-serif; line-height: 18px;\">&nbsp;</p>\r\n<p>&nbsp;</p>','2540','护肤彩妆	美容护肤	面部护理套装','康婷瑞倪维儿','0','','','1','0',null,'1403553154','1403554721','365','data/files/store_358/goods_15/small_201406241146556690.jpg','1','2534','2535','2540','0','1499.00','300','');
