INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('303','392','material','反渗透矿化净水机','<p>【产品特点】</p>\r\n<p align=\"left\">（1）采用先进的反渗透技术进行水处理；</p>\r\n<p align=\"left\">（2）净水可直接饮用，适用于家庭生活用水；</p>\r\n<p align=\"left\">（3）本机在反渗透的基础上增加矿化复合滤芯，使净水呈弱碱性，同时补充人体所需的微量元素；</p>\r\n<p align=\"left\">（4）采用炫彩指示功能，方便实用，时尚炫目；</p>\r\n<p align=\"left\">（5）外观设计时尚美观、体积小、结构紧凑、安装方便；</p>\r\n<p align=\"left\">（6）独有的11寸大滤瓶与机壳合于一体技术，减少了水质差的地区滤芯过早堵塞的现象；</p>\r\n<p align=\"left\">（7）采用滤芯寿命报警功能，滤芯的更换时间由电脑版自动控制；</p>\r\n<p align=\"left\">（8）使用寿命长，适用范围广。</p>\r\n<p>&nbsp;</p>\r\n<p>【主要技术参考值】</p>\r\n<p align=\"left\">适用水源：市政自来水</p>\r\n<p align=\"left\">适用水压：0.1-0.4MPa</p>\r\n<p align=\"left\">工作压力：0.5-0.8MPa</p>\r\n<p align=\"left\">净水流量：12L/H(75GPD) (25℃.0.15MPa)</p>\r\n<p align=\"left\">额定总净水量：3000L</p>\r\n<p align=\"left\">压力桶容量：3.0G</p>\r\n<p align=\"left\">适用电源：AC220V.50Hz</p>\r\n<p align=\"left\">额定功率：30W</p>\r\n<p align=\"left\">执行标准：Q/TAJRR001-2012</p>\r\n<p align=\"left\">外形尺寸：360mm*460mm*191mm</p>','1650','家电办公	厨房电器	净水器','金日','0','','','1','0',null,'1403835760','1404510636','373','data/files/store_392/goods_57/small_201406271820572915.jpg','1','1634','1648','1650','0','2480.00','800','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('304','392','material','QQ清新净水机','<p><strong>【技术参数】</strong></p>\r\n<p>&nbsp;&nbsp; 型号：QQ型</p>\r\n<p>&nbsp;&nbsp; 货号：JRC-1</p>\r\n<p>&nbsp;&nbsp; 适用水源：市政自来水</p>\r\n<p>&nbsp;&nbsp; 适用水压：0.15-0.3MPa</p>\r\n<p>&nbsp;&nbsp; 净水流量：60L/H</p>\r\n<p>&nbsp;&nbsp; 额定总净水量：3000L</p>\r\n<p>外形尺寸：390mm*354mm*128mm</p>\r\n<p><strong>【产品特点】</strong></p>\r\n<p>滤芯功能</p>\r\n<p><strong>第一级：高品质PP棉纤维滤芯</strong></p>\r\n<p>功能：孔径为5微米，精密滤芯，彻底除去水中的残留的泥沙，铁锈，沙石，悬浮物，杂质等。</p>\r\n<p><strong>第二级：活性炭滤芯</strong></p>\r\n<p>功能：清除水中残留的农药，化肥，及有机污染物，去除氯，异色，异味，同时增加水的甘甜可口感。</p>\r\n<p><strong>第三级：离子交换树脂滤芯</strong></p>\r\n<p>功能：能将钙，镁元素含量较高的硬水通过离子交换去除钙美离子，消除结垢，茶垢，使硬水转化成软水，避免人体长期饮食硬水。</p>\r\n<p><strong>第四级：</strong><strong>陶瓷滤芯</strong><strong></strong></p>\r\n<p>功能：于对自来水进行过滤、抗菌和活化处理，同时滤除水中的重金属离子，有害的余氯和悬浮污染物、有机化学物质、颜色与异味。</p>\r\n<p><strong>第五级：中空纤维超滤膜</strong></p>\r\n<p>功能：使用超微孔径高分子材料制成不对称半透膜，呈中空毛细管状，管壁弥补微孔，孔径为0.01微米，高效滤除水中的微粒丶悬浮物及热原细菌丶大肠杆菌等病毒，彻底清除高分子有机物。</p>\r\n<p><strong>第六级：纳米能量滤芯</strong></p>\r\n<p>功能：补充人体所需微量元素，同时调整水体的PH值使之成为弱碱性。</p>\r\n<p><strong>第七级：后置活性炭</strong></p>\r\n<p>功能：抑菌，去除水中异味使水更甘甜可口。</p>\r\n<p><strong>第四级陶瓷滤芯的清洗：</strong></p>\r\n<p>拆下滤筒，并拧开滤筒，小心翼翼拧下陶瓷滤芯。左手握住陶瓷滤芯的一头，打开自来水龙头，一边冲洗一边用水洗砂布磨擦陶瓷表面，直至黑色消除，还原为原来的白色。之后再重新装上去即可。</p>','1650','家电办公	厨房电器	净水器','金日','0','','','1','0',null,'1403835983','1404510596','374','data/files/store_392/goods_46/small_201406271824066737.jpg','1','1634','1648','1650','0','2580.00','600','');
