INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('309','392','material','艾力健胶囊,纯积分，仅需220分','<p>有效缓解体力疲劳<strong></strong></p>\r\n<p>【产品特点】</p>\r\n<p>金日牌艾力健胶囊是以蝙蝠蛾被毛孢菌丝体粉、人参、熟地黄、枸杞子、淫羊藿、巴戟天、蜂王浆冻干粉、淀粉为主要原料制成的保健食品，经动物功能试验证明，具有缓解体力疲劳的保健功能。</p>\r\n<p>&middot;权威研制&nbsp; 汇通古今</p>\r\n<p>组织国内外26位著名健康营养专家，历经3年研制而成。</p>\r\n<p>&middot;名贵药材&nbsp; 珍如黄金</p>\r\n<p>艾力健经由蝙蝠蛾被毛孢菌丝体粉、人参、枸杞、淫羊藿等名贵药材科学组方，特别是蝙蝠蛾被毛孢菌丝体粉被誉为珍稀药材中的&ldquo;软黄金&rdquo;。</p>\r\n<p>&middot;水井理论&nbsp; 振聋发聩</p>\r\n<p>艾力健运用金日独创的权威保健理论&ldquo;水井论&mdash;&mdash;打水+蓄水&rdquo;，补充肾气、肾元，抵御过度疲劳对生命基础的伤害，源源不断地补充人体之消耗；充足人体精、气、神，形成人体良性循环。（人体就像一口井，&ldquo;肾元&rdquo;就是井中水。每天不断打水用，水越打越少，肾元亏虚，怎会不疲劳？要保持旺盛的活力，就要增强蓄水能力！打水+蓄水，一边消耗一边补充，源源不断，活得更饱满！）</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】蝙蝠蛾被毛孢菌丝体粉、人参、熟地黄、枸杞子、淫羊藿、巴戟天、蜂王浆冻干粉、淀粉</p>\r\n<p>【标志性成分及含量】每100g含：腺苷23.3mg、总皂甙1.65g</p>\r\n<p>【保健功能】缓解体力疲劳</p>\r\n<p>【适宜人群】易疲劳者</p>\r\n<p>【不适宜人群】少年儿童</p>\r\n<p>【食用方法及食用量】 每日2次，每次4粒，早晚食用</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403836619','1403836619','379','data/files/store_392/goods_126/small_201406271835269171.jpg','1','2595','2596','2604','0','0.00','220','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('310','392','material','爱妻康延口服液','<p>让女人从内而外添姿彩</p>\r\n<p>【产品特点】</p>\r\n<p>金日爱妻牌康延口服液是以西洋参、白芍、酸枣仁、五味子、速溶珍珠粉、黑豆、蜂蜜为主要原料制成的保健食品，经功能试验证明，具有改善睡眠、延缓衰老的保健功能。</p>\r\n<p>女性因为特殊的生理条件，无论是哪个年龄段的女人，都不同程度存在睡眠不佳、容颜憔悴、内分泌失调等困扰，稍不注意就会产生各种疾病影响女性健康，美丽也大打折扣。</p>\r\n<p>美丽更需要由内而外的呵护和调理，以其浓妆淡抹地用化妆品打理自己的面容，不如让自己拥有一个健康的身体再略施粉黛。由内而外展现美的本质，全方位的&ldquo;美丽&rdquo;让女性更自信更添姿彩！</p>\r\n<p>金日爱妻牌康延口服液组方根据现代医学理论，以白芍、酸枣仁、五味子、速溶珍珠粉、黑豆提取物，再辅以西洋参全面调补，综合调理，是女性安神养颜佳品，特别适合睡眠不佳、容颜憔悴、内分泌失调女性及更年期女性服用。</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】蒸馏水、西洋参、白芍、酸枣仁、五味子、速溶珍珠粉、黑豆、蜂蜜</p>\r\n<p>【功效成份及含量】每100ml含：总皂甙（以人参皂甙Re计）190mg</p>\r\n<p>【保健功能】改善睡眠、延缓衰老<br />【适宜人群】睡眠状况不佳者、中老年人<br />【不适宜人群】少年儿童、孕妇及哺乳期妇女<br />【食用方法及食用量】每日2-3次，每次20ml；本品可有少量沉淀，食用前摇匀。</p>','2606','保健品	保健食品	口服液','','0','','','1','0',null,'1403836736','1404510463','380','data/files/store_392/goods_37/small_201406271837177499.jpg','1','2595','2596','2606','0','165.00','120','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('311','392','material','金日牌破壁灵芝孢子粉胶囊','<p>【产品特点】</p>\r\n<p>金日牌破壁灵芝孢子粉胶囊是以破壁灵芝孢子粉、羟丙甲纤维素为主要原料制成的保健食品，经动物功能试验证明，具有增强免疫力的保健功能。</p>\r\n<p><strong>&middot;原料天然纯正</strong>&mdash;&mdash;金日牌蜂灵胶囊采用的灵芝产至武夷山原生态自然环境下杂交、培育而成的符合国际标准，品质优良无污染、纯天然的赤芝品种，并经过精心挑选和严格检测。</p>\r\n<p><strong>&middot;低温物理超微破壳技术精制而成</strong>&mdash;&mdash;超微破壁灵芝孢子粉吸收率在90%以上，效果是未破壁孢子粉的75倍。</p>\r\n<p>金日为什么要采用破壁灵芝孢子粉？</p>\r\n<p>灵芝孢子细胞壁质密度高，直接服用尚未破壁的孢子粉，人体内的消化系统吸收不佳。人体内的酶是分解消化的物质，而科学研究证明：人体的酶需要24小时才能分解灵芝孢子粉，但通常口服尚未破壁的孢子粉在胃肠内只能停留4-6小时，加之孢子的体积极小，体型为卵状，很快就会被排出体外。细胞壁达到超微级，服用后立竿见影，吸收快，更为奇妙的是，在排泄过程中，小肠对超微级破壁灵芝孢子粉能进行二次吸收利用。</p>\r\n<p><strong>&middot;高分子生物膜包裹技术</strong>&mdash;&mdash;羟丙甲纤维素，一种高分子生物膜，遇水溶解，安全无毒，能够包裹破壁后的孢子粉，防止被有机酸氧化。</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】破壁灵芝孢子粉、羟丙甲纤维素</p>\r\n<p>【标志性成分及含量】每100g含：粗多糖0.7g</p>\r\n<p>【保健功能】增强免疫力</p>\r\n<p>【适宜人群】免疫力低下者</p>\r\n<p>【不适宜人群】少年儿童</p>\r\n<p>【食用方法及食用量】 每日2次，每次3粒</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403836822','1404510442','381','data/files/store_392/goods_152/small_201406271839122344.jpg','1','2595','2596','2604','0','240.00','180','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('312','392','material','博m伊美补水滢润系列套装','<p><strong>套装包含：补水滢润养颜水&nbsp; 100ml/瓶</strong></p>\r\n<p><strong>补水滢润养颜精萃精华&nbsp; 30ml/瓶</strong></p>\r\n<p><strong>补水滢润养颜精萃乳&nbsp; 50ml/瓶</strong></p>\r\n<p><strong>补水滢润养颜精萃霜&nbsp; 50g/罐</strong></p>\r\n<p><strong>【产品介绍】</strong></p>\r\n<p>植物精华匀透肌肤&nbsp; 点滴养颜呈现自然水嫩</p>\r\n<p>富含西洋参、白茶、白茯苓、红景天、洋甘菊等鲜活萃取精华，能快速长效渗透至肌肤深层，并直接参与细胞的新陈代谢，使肌肤加倍锁紧水分、更富弹性，修复肌肤自有储水能力，令肌肤绽放明露般水灵光彩。</p>\r\n<p><strong>&middot;补水滢润养颜水</strong></p>\r\n<p>适用人群：适用各种肌肤类型</p>\r\n<p>产品功效：蕴含精纯天然植物成分，结合现代生物锁水保湿科技，满足肌肤持续水润渴求。</p>\r\n<p>使用方法：既可洁面后每日使用，亦可集中密集做面膜护理。</p>\r\n<p><strong>&middot;补水滢润养颜精萃精华</strong></p>\r\n<p>适用人群：适用各种肌肤类型</p>\r\n<p>产品功效：蕴含精纯天然植物成分，具有强力抗氧化功效，有效减退黯黄的同时抑制酪氨酸酶的活性，有效美白肌肤。可对抗电脑辐射带来的干燥，水油失衡，肌肤粗糙，暗淡等肌肤问题。</p>\r\n<p>使用方法：每日早晚在精萃水后使用，之后进行补水滢润养颜的日常护肤。</p>\r\n<p><strong>&middot;补水滢润养颜精萃乳</strong></p>\r\n<p>适用人群：适用各种肌肤类型</p>\r\n<p>产品功效：轻盈玉润质地，天然植物精萃，能修复并延缓老化、激活缺氧缺水细胞，隐退暗黄，日渐透白均匀。</p>\r\n<p>使用方法：每日早晚在精华液后使用。请使用前充分摇匀，以使乳液更易倒出。</p>\r\n<p>&middot;<strong>补水滢润养颜精萃霜</strong></p>\r\n<p>适用人群：干燥天气或干性皮肤适用</p>\r\n<p>产品功效：蕴含精纯天然植物成分，提高肌肤抵抗力并有效锁水，均匀涂抹于面部和颈部，使肌肤平滑舒适。</p>\r\n<p>使用方法：每日早晚在洁面后，取适量均匀涂于面、颈部，轻轻按摩至完全吸收。</p>','2544','护肤彩妆	美容护肤	面部精华','','0','','','1','0',null,'1403836989','1404510425','382','data/files/store_392/goods_39/small_201406271840398353.jpg','1','2534','2535','2544','0','1800.00','1600','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('313','392','material','丝然测试护垫,纯积分，仅需30分','<p align=\"left\">【产品特点】女性阴道正常PH值为3.8&mdash;4.4之间，在有炎症的情况下会改变正常的PH值。女性可通过对自身分泌物的自测，解析私处生理环境的健康。</p>\r\n<p align=\"left\">【使用方法】1、月经干净后第7天为最佳检测时间，建议在晨尿之后使用；</p>\r\n<p align=\"left\">2、自测时间不超过10分钟，通常情况下有分泌物时即可测出身体健康状况。</p>\r\n<p align=\"left\">3、正常检测护垫的颜色为黄绿色或蓝色，不正常的检测护垫颜色为黄色或蓝紫色。结果显示异常请寻求</p>\r\n<p align=\"left\">【卫生执行标准】GB15979-2002</p>\r\n<p align=\"left\">【产品执行标准】GB/T8939-2008</p>','1386','日用百货	洗护清洁	卫生巾','','0','','','1','0',null,'1403837087','1404510487','383','data/files/store_392/goods_11/small_201406271843316090.jpg','1','1337','1385','1386','0','0.00','30','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('314','392','material','金日红大红袍','<p>【产品简介】</p>\r\n<p>金日红大红袍属青茶类（俗称乌龙茶类）、半发酵茶类。金日红大红袍是生长在自然与文化双遗产地&mdash;&mdash;福建武夷山。武夷山坐落在福建省东北部，有&ldquo;奇秀甲于东南&rdquo;之誉。群峰相连，峡谷纵横，九曲溪萦回其间，气候温和，冬暖夏凉，雨量充沛。武夷不独以山水之奇而奇，更以茶产之奇而奇。武夷山悬崖绝壁，深坑巨谷。利用岩凹、石隙、石缝，沿边砌筑石岸，构筑&ldquo;盆栽式&rdquo;茶园。武夷岩茶也是中国十大名茶之一，武夷岩茶现被统一称为大红袍。</p>\r\n<p>&nbsp;</p>\r\n<p>【品质特征】</p>\r\n<p>外形弯条型，色泽乌褐或带墨绿、或带沙绿、或带青褐、或带宝色。条索紧结、或细紧或壮结，汤色橙黄至金黄、清澈明亮。香气带花、果香型，锐则浓长、清则幽远，或似水蜜桃香、兰花香、桂花香、乳香等。滋味醇厚滑润甘爽，带特有的&ldquo;岩韵&rdquo;。叶底软亮、呈绿叶红镶边、或叶缘红点泛现。不同的茶树品种还带有不同的品种特征。</p>\r\n<p>&nbsp;</p>\r\n<p>【产品特点】</p>\r\n<p>科学合理饮用&ldquo;大红袍&rdquo;，有助于明目益思、提神醒脑、利尿、抗辐射、助消化、降血压等。国际友人曾赞叹武夷岩茶为&ldquo;万物之甘露，神奇之药物&rdquo;。</p>','2597','保健品	保健食品	茶','','0','','','1','0',null,'1403837215','1404510408','384','data/files/store_392/goods_112/small_201406271845124551.jpg','1','2595','2596','2597','0','330.00','350','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('315','392','material','参茶物语金日牌美国洋参袋泡茶','<p>【产品特点】</p>\r\n<p>金日牌美国洋参袋泡茶是以优质美国洋参为原料，采用现代先进工艺精制而成的保健食品，经功能试验证实，本品具有抗疲劳的保健作用。经检测，本品不含兴奋剂、激素，食用方便，口感纯正。</p>\r\n<p>&middot;取优质西洋参中段，按科学要求粉碎成三种不同大小的颗粒，并按比例混合后装袋制成。可使产品三次浸泡，每次所泡出的液体中有效成分含量相近；</p>\r\n<p>&middot;取代茶叶茶、咖啡、奶茶等休闲产品，提神效果佳且更利于健康；</p>\r\n<p>&middot;小袋独立包装，携带更方便；</p>\r\n<p>&middot;开水浸泡，可多次浸泡。</p>\r\n<p>【配料表】西洋参&nbsp;&nbsp;</p>\r\n<p>【保健作用】 抗疲劳</p>\r\n<p>【功效成份及含量】 每100g含人参皂甙&ge;3g</p>\r\n<p>【适宜人群】 易疲劳者&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>【不适宜人群】 儿童</p>\r\n<p>【食用方法及食用量】 开水冲泡作茶饮，每日3次，每次1袋，可多次冲泡</p>','2597','保健品	保健食品	茶','','0','','','1','0',null,'1403837412','1404510387','385','data/files/store_392/goods_33/small_201406271847138580.jpg','1','2595','2596','2597','0','65.00','60','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('316','392','material','胶原蛋白固体饮料','<p>【产品特点】</p>\r\n<p>蛋白物语是以优质胶原蛋白、脱脂奶粉、低聚果糖等为主要原料制成的固体饮料，品质纯正、口感香浓、无加蔗糖，忌糖人士及糖尿病人群也可服用，不含色素和防腐剂，是现代人推崇的营养佳品。</p>\r\n<p>【主要原料】</p>\r\n<p>胶原蛋白、脱脂奶粉、低聚果糖、速溶红茶粉、结晶果糖、麦芽糊精、植脂末、食用香料。</p>\r\n<p>【食用方法及食用量】 取本品1袋，加温开水约150ml，搅匀即可</p>\r\n<p>&nbsp;</p>\r\n<p><strong>【认识蛋白质】</strong></p>\r\n<p>胶原蛋白(collagen)是一种高分子蛋白质，是人体组织结构的主要成分之一，也是人体内含量最多的一种蛋白质，约占人体蛋白质总量的25-33%，相当于人体体重的6%，它遍及全身的各个组织器官，如：皮肤、骨骼、软骨、韧带、角膜、各种内膜、筋膜等，是维持皮肤与组织器官形态、结构的主要成分，也是修复各损伤组织的重要原料物质。</p>\r\n<p>【如何有效补充胶原蛋白】</p>\r\n<p>食物获取。比如牛蹄筋、猪蹄、鸡翅、鸡皮、鱼皮、鱼翅及软骨等可以在一定程度上补充胶原蛋白，但食物中的胶原蛋白因分子量太大，能被有效吸收的极少，而猪蹄、肉皮属高胆固醇、高脂肪食品，长期食用不仅会引起心脑血管疾病，还容易使人发胖，令渴望身材苗条的女性望而生畏。</p>\r\n<p>胶原蛋白制品是从动物体内提取的胶原蛋白，分子量小，易被人体吸收，是成人补充胶原蛋白的最佳选择。</p>','2607','保健品	保健食品	冲剂','','0','','','1','0',null,'1403837533','1404510365','386','data/files/store_392/goods_29/small_201406271850294052.jpg','1','2595','2596','2607','0','140.00','100','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('317','392','material','金日牌西洋参含片,','<p>【产品特点】</p>\r\n<p>金日牌西洋参含片是以西洋参为主要原料制成的保健食品，经功能试验证明，具有抗疲劳的保健功能。</p>\r\n<p>1.通过口腔粘膜下的毛细血管直接吸收，无需经过消化道，可减少消化液对有效成分的破坏，提高利用率且起效快；</p>\r\n<p>2.随时随地、服用方便，味道甜中带苦、清凉发散；</p>\r\n<p>3.添加薄荷油成分使产品更快起到提神醒脑效果，特别适宜对糖份摄取无禁忌的中高考学生、白领人士、商务人士、司机等需要时刻保持头脑清醒的人士。</p>\r\n<p>【配料表】西洋参、葡萄糖、蔗糖、淀粉、羧甲基纤维素钠、薄荷油</p>\r\n<p>【功效成分及含量】每100g含：总皂甙（以人参皂甙Re计）1.52g</p>\r\n<p>【保健作用】抗疲劳</p>\r\n<p>【适宜人群】易疲劳者</p>\r\n<p>【不适宜人群】少年儿童</p>\r\n<p>【食用方法及食用量】每次0.6&mdash;1.2g，每日3次。需时可增加用量，口中含食</p>','2605','保健品	保健食品	片剂','','0','','','1','0',null,'1403837709','1404510340','387','data/files/store_392/goods_184/small_201406271853044546.jpg','1','2595','2596','2605','0','110.00','70','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('318','392','material','金日牌蜂灵胶囊','<p>蜂胶+灵芝=双重守护您的健康</p>\r\n<p>【产品特点】</p>\r\n<p>金日牌蜂灵胶囊是以蜂胶、灵芝孢子粉、灵芝多糖为主要原料制成的保健食品，经动物功能试验证明，具有增强免疫力的保健功能。</p>\r\n<p>&middot;选材精细&mdash;&mdash;其主要原料蜂胶和灵芝天然、纯正，并经过精心挑选和严格检测。</p>\r\n<p>&middot;低温物理超微破壳技术精制而成&mdash;&mdash;超微破壁灵芝孢子粉吸收率在90%以上，效果是未破壁孢子粉的75倍。</p>\r\n<p>&middot;对蜂胶的提纯工艺&mdash;&mdash;采用冷冻干燥的处理程序并使用减温减压去水分的方法祛除乙醇，最大限度地保留了蜂胶的有效成分。</p>\r\n<p>&middot;蜂胶+灵芝&mdash;&mdash;解决了单一配方吸收不完全、有效成分利用率不高的问题。</p>\r\n<p>蜂胶和灵芝强强联手，协同作战，优势互补，品质稳定。蜂胶能有效抑制和消灭病菌，从而减轻免疫系统的负担，而灵芝能激活整个免疫系统的运转和免疫细胞的活性，二者结合相当于对免疫系统进行全面整改和补充；另，蜂胶中加入灵芝能有效减轻或清除蜂胶中铅、汞、砷等重金属残留积蓄的问题。</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】蜂胶、灵芝孢子粉、灵芝多糖</p>\r\n<p>【标志性成分及含量】每100g含：总黄酮3.19g、粗多糖0.587g</p>\r\n<p>【保健功能】增强免疫力</p>\r\n<p>【适宜人群】免疫力低下者</p>\r\n<p>【不适宜人群】少年儿童</p>\r\n<p>【食用方法及食用量】每日2次，每次2粒</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403837796','1404510324','388','data/files/store_392/goods_134/small_201406271855347242.jpg','1','2595','2596','2604','0','200.00','140','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('319','392','material','金日牌金日钙咀嚼片,','<p>优质钙源&nbsp; 补钙佳品</p>\r\n<p>【产品特点】</p>\r\n<p>金日牌钙咀嚼片是以乳钙等为主要原料制成的保健食品，具有补钙的保健功能。</p>\r\n<p>&middot;选材优质&mdash;&mdash;采用新西兰优质牛乳中提取的钙质。</p>\r\n<p>&middot;现代先进技术精制而成&mdash;&mdash;无重金属污染，纯天然，食用安全。</p>\r\n<p>&middot;科学配方，钙磷比例1.8:1，符合国际标准。</p>\r\n<p>&middot;配以酪蛋白磷酸肽和维生素D3，以促进钙质的溶解和吸收。</p>\r\n<p>&middot;采用木糖醇为甜味剂，保证口感，防止蛀牙；同时糖尿病患者也可食用。</p>\r\n<p>&middot;金日钙咀嚼片含钙量为54毫克/粒，科学合理，少量多餐，利于吸收，减少肝肾负担。</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】异麦芽酮糖醇、乳钙、酪蛋白磷酸肽、阿斯巴甜（含苯丙氨酸）、维生素D<sub><span style=\"font-size: x-small;\">3</span></sub></p>\r\n<p>【功效成份及含量】每片中含钙54mg</p>\r\n<p>【保健功能】补钙</p>\r\n<p>【适宜人群】需要补钙者</p>\r\n<p>【不适宜人群】苯酮尿症<a name=\"_GoBack\"></a>患者</p>\r\n<p>【食用方法及食用量】 每日2次，每次2- 4片，嚼食</p>\r\n<p>【注意事项】 1、本品不能代替药物</p>\r\n<p>2、不宜超过推荐量或与同类营养素补充剂同时食用</p>','2605','保健品	保健食品	片剂','','0','','','1','0',null,'1403837872','1404510303','389','data/files/store_392/goods_11/small_201406271856519121.jpg','1','2595','2596','2605','0','80.00','60','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('320','392','material','金日牌西洋参含片','<p>【产品特点】</p>\r\n<p>金日牌西洋参含片是以西洋参为主要原料制成的保健食品，经功能试验证明，具有抗疲劳的保健功能。</p>\r\n<p>1.通过口腔粘膜下的毛细血管直接吸收，无需经过消化道，可减少消化液对有效成分的破坏，提高利用率且起效快；</p>\r\n<p>2.随时随地、服用方便，味道甜中带苦、清凉发散；</p>\r\n<p>3.添加薄荷油成分使产品更快起到提神醒脑效果，特别适宜对糖份摄取无禁忌的中高考学生、白领人士、商务人士、司机等需要时刻保持头脑清醒的人士。</p>\r\n<p>【配料表】西洋参、葡萄糖、蔗糖、淀粉、羧甲基纤维素钠、薄荷油</p>\r\n<p>【功效成分及含量】每100g含：总皂甙（以人参皂甙Re计）1.52g</p>\r\n<p>【保健作用】抗疲劳</p>\r\n<p>【适宜人群】易疲劳者</p>\r\n<p>【不适宜人群】少年儿童</p>\r\n<p>【食用方法及食用量】每次0.6&mdash;1.2g，每日3次。需时可增加用量，口中含食</p>','2605','保健品	保健食品	片剂','','0','','','1','0',null,'1403838023','1404510282','390','data/files/store_392/goods_87/small_201406271858075371.jpg','1','2595','2596','2605','0','120.00','60','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('321','392','material','参洁西洋参滋养润发精华素,','<p>【产品特点】<br />&bull;富含西洋参精华和海洋鱼皮胶原低聚肽，采用特殊制作工艺，能够滋养、护理发丝；<br />&bull;补充和保持头发所需水分，形成柔润保护膜，加强养护作用；<br />&bull;帮助改善枯黄开叉、脆弱受损的发质，使秀发更加柔润亮泽。 <br />【成分】<br />水、鲸蜡硬脂醇、聚二甲基硅氧烷、硬脂基三甲基铵甲基硫酸盐、氨乙基氨丙基聚二甲基硅氧烷、季铵盐-27、西洋参根提取物、季戊四醇四椰油酸酯、甘油、水解胶原、氧化聚乙烯、香精、EDTA 二钠、柠檬酸、甲基异噻唑啉酮、甲基氯异噻唑啉酮<br />【使用方法】<br />洗发后，取适量润发精华素均匀轻揉于秀发，停留2-5分钟后再以清水冲洗干净即可。<br />【注意事项】<br />如不慎入眼，立即用清水冲洗，若需要，请寻求适当医学治疗；请勿置于儿童可取处，谨防儿童误食。<br />【贮存条件】置通风干燥处，避免阳光直射。</p>\r\n<p>&nbsp;</p>','1394','日用百货	洗护清洁	洗发水','','0','','','1','0',null,'1403838167','1404510262','391','data/files/store_392/goods_43/small_201406271900432551.jpg','1','1337','1385','1394','0','40.00','30','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('322','392','material','禾野综合酵素三益菌,','<p>禾野综合酵素ABF三益菌含有多种酵素，能将食物中三大营养素的碳水化合物（淀粉、糖类、纤维素）、蛋白质、脂肪等完全消化与吸收，促进食欲，平衡肠道菌群，促进新陈代谢，并可调整体质，养颜美容。</p>\r\n<p>【配料表】</p>\r\n<p>麦芽糊精、乳糖、低聚木糖、&alpha;-淀粉酶、脂肪酶、纤维素酶、蛋白酶、嗜酸乳杆菌、长双歧杆菌、嗜热链球菌</p>\r\n<p>【食用方法与食用量】</p>\r\n<p>成人每次1包，每日1次，可依体质酌量增加1-3次，饭后服用。2、婴幼儿每次1/3包，每日1-3次。（加入牛奶宜40℃以下，并迅速食用）可依体质酌量增加。</p>','2607','保健品	保健食品	冲剂','','0','','','1','0',null,'1403838273','1404508995','392','data/files/store_392/goods_182/small_201406271903025843.jpg','1','2595','2596','2607','0','180.00','100','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('323','392','material','胶原蛋白晶体','<p align=\"left\">【产品成分】</p>\r\n<p align=\"left\">胶原蛋白、木瓜蛋白酶、水果酵素、硬脂酸、肉豆蔻酸、碱水、椰子油、蜂蜜、卵磷脂、三乙酸胺、甘油、维他命C、维他命E、橄榄油、荷荷巴油、杏仁油。</p>\r\n<p align=\"left\">【产品特点】</p>\r\n<p align=\"left\">干性肌肤、粗糙肌质之选。能使活性酵素迅速渗入肌肤底层，并补充酵素蛋白、促进皮肤细胞活络与代谢，从而改善干性、粗糙肌质，提升肌肤对保养品的吸收能力，维持肌肤柔嫩光滑，呈现自然均匀的白皙洁净。</p>\r\n<p align=\"left\">【注意事项】</p>\r\n<p align=\"left\">1.使用时，请勿将晶体直接冲水或浸泡于水，使用后请将晶体上的泡沫清除。</p>\r\n<p align=\"left\">2.使用后，请从晶体盒里取出垫片，将晶体置于垫片上，并放置于阴凉、通风、干燥处</p>','2542','护肤彩妆	美容护肤	洁面','','0','','','1','0',null,'1403838409','1404508936','393','data/files/store_392/goods_96/small_201406271904564308.jpg','1','2534','2535','2542','0','80.00','40','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('324','392','material','金日牌金维他含片','<p>【产品特点】</p>\r\n<p>金日牌金维他含片是以维生素A、维生素C、维生素E、白砂糖、柠檬酸、柠檬香精、红花黄色素为主要原料制成的保健食品，具有补充维生素A、维生素C、维生素E的保健功能。</p>\r\n<p>【配料表】维生素C、维生素E、维生素A、白砂糖、柠檬酸、柠檬香精、红花黄色素</p>\r\n<p>【功效成份及含量】每1片含：维生素C20.8mg、维生素E2.4mg、维生素A121&mu;g</p>\r\n<p>【保健功能】补充维生素C、维生素E、维生素A</p>\r\n<p>【适宜人群】需要补充维生素C、维生素E、维生素A者</p>\r\n<p>【食用方法及食量】每日3次，每次1片，含食</p>\r\n<p>【注意事项&#093;】 1、本品不能代替药物</p>\r\n<p>2、不宜超过推荐量或与同类营养素补充剂同时食用</p>','2605','保健品	保健食品	片剂','','0','','','1','0',null,'1403838486','1404510238','394','data/files/store_392/goods_25/small_201406271907053144.jpg','1','2595','2596','2605','0','60.00','45','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('325','392','material','碧喷口咽喷剂','<p align=\"left\">【产品特点】</p>\r\n<p>&middot;精提天然植物精油 渗透快速&nbsp; 功效显著</p>\r\n<p>其主要成分采用超临界萃取技术，精提澳洲茶树、香薷、桉叶等纯天然植物的纳米微乳精油，能够在口腔中溶解黏液，更快地渗入咽喉表面组织，增强精油的渗透力，快速渗透，并极为有效地进出身体，不会残留任何毒素。</p>\r\n<p>&middot;气味芳香&nbsp; 清新口气&nbsp; 舒爽咽喉</p>\r\n<p>清凉香气，独特的芳香温和无刺激，有效地清除口腔中残留的异臭，提高口咽的免疫力。同时可以亲密感受来自澳洲精油的纯天然气息，令咽喉备受呵护，处于湿润舒爽的最佳状态。</p>\r\n<p>&middot;健康时尚&nbsp; 随身携带</p>\r\n<p>外形精美时尚，是健康生活的好伴侣。无论是旅途，还是日常生活，随手喷一喷，拒绝病菌，远离咽喉问题。</p>\r\n<p>&nbsp;</p>\r\n<p>【主要成份】</p>\r\n<p>澳洲茶树油（Melaleuca altemifolia）、香薷油（Moslae herba）、桉叶油（Eucalyptus）等纯植物精油</p>\r\n<p>【使用范围】</p>\r\n<p>口腔致病菌的大量繁殖，会带来咽痒、咽干、口腔异味等问题。本品有助于抑制口腔致病菌，消除口腔异味，保持口气清新。</p>\r\n<p>【使用方法】</p>\r\n<p>&nbsp;直接喷于口腔或咽部，适时使用</p>\r\n<p>【注意事项】</p>\r\n<p>避免入眼；置儿童不能触及之处</p>','2536','护肤彩妆	美容护肤	身体护理','','0','','','1','0',null,'1403838588','1404510220','395','data/files/store_392/goods_102/small_201406271908221963.jpg','1','2534','2535','2536','0','18.00','20','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('326','392','material','金日牌芦荟胶','<p><strong>享受纯天然芦荟带给你的全新护肤体验<br /></strong>金日牌芦荟胶以库拉索芦荟凝胶冷冻干燥粉，配合西洋参萃取物等多种植物提取精华研制而成的草本凝胶。使用后令肌肤倍感清爽、嫩滑，并能舒缓皮肤各种状况，防止皮肤干燥粗糙，保持肌肤水润光泽状态。<br />【产品特点】<br />作为一款卓有成效的护肤品，本款芦荟胶具有消炎、杀菌；镇静、止痒、复健、抗过敏、加速伤口愈合，以及非常显著的辅助美容作用。真正是一款老少皆宜、四季常备的优质产品。<br />【产品成分】<br />水、库拉索芦荟叶汁粉、丁二醇、丙二醇、西洋参根提取物、甘草根提取物、假叶树根提取物、积雪草提取物、金盏花提取物、甜菜根提取物、金黄洋甘菊提取物、聚谷氨酸、&beta;-葡聚糖、尿囊素、EDTA二钠、丙烯酸（酯）类/C10-30烷醇丙烯酸酯交联聚合物、三乙醇胺、甲基异噻唑啉酮、碘丙炔醇丁基氨甲酸酯、苯扎溴铵。<br />【使用方法】皮肤洗净擦干后，取适量芦荟胶均匀涂抹于肌肤即可。 <br />【储存条件】请将本品置于阴凉干燥处，避免阳光直射。<br />【注意事项】避免接触眼睛</p>\r\n<p>&nbsp;</p>','2536','护肤彩妆	美容护肤	身体护理','','0','','','1','0',null,'1403838707','1404510203','396','data/files/store_392/goods_31/small_201406271910312609.jpg','1','2534','2535','2536','0','45.00','30','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('327','392','material','酪梨嫩白晶体','<p align=\"left\">【产品成分】</p>\r\n<p align=\"left\">酪梨萃取物、木瓜蛋白酶、迷迭香精油、椰子油、蜂蜜、卵磷脂、荷荷巴油、可可油、水果酵素、乳油木果油。</p>\r\n<p align=\"left\">【产品特点】</p>\r\n<p align=\"left\">消除身体异味，洗净夏日汗臭味。散发浓郁的酪梨果香味，让肌肤享受天然SPA感觉；细腻丰富的泡沫，温柔呵护肌肤，清爽不黏腻，滋润肌肤，去除老化角质并促进肌肤滑嫩与亮泽；是夏日美白圣品，赋予肌肤活力、嫩滑细腻的触感，让您尽享一身的白皙洁净。</p>\r\n<p align=\"left\">【注意事项】</p>\r\n<p align=\"left\">1.使用时，请勿将晶体直接冲水或浸泡于水，使用后请将晶体上的泡沫清除。</p>\r\n<p align=\"left\">2.使用后，请从晶体盒里取出垫片，将晶体置于垫片上，并放置于阴凉、通风、干燥处</p>','2540','护肤彩妆	美容护肤	面部护理套装','','0','','','1','0',null,'1403838788','1404510187','397','data/files/store_392/goods_129/small_201406271912093422.jpg','1','2534','2535','2540','0','80.00','40','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('328','392','material','广润牌牦牛骨粉胶囊','<p>【产品特点】</p>\r\n<p>广润牌牦牛骨粉胶囊是以牦牛骨粉、硬脂酸镁为主要原料制成的保健食品，经动物功能试验证明，具有增加骨密度的保健功能。</p>\r\n<p>&middot;新一代络合钙独步全球，它是用现代生物工程技术&mdash;&mdash;酶解法制得，对检疫过的牛羊鲜骨进行全面深加工处理的天然补钙养骨产品，钙及其他矿物质均以络合物形式存在，便于吸收和有效利用。纯天然、有效养分完全保留，天然合理，相似相容，亲和力强。含有全骨营养素、人体必需微量元素、氨基酸、蛋白质、骨胶原和各种维生素等，并含有免疫活性因子，11个以上的活化骨细胞及能促进内分泌、提升钙表达能力的因子等，具有养骨、成骨的功效。不沉积，并具有软化血管，散结石等机能。</p>\r\n<p>&middot;新一代产品整体采用低温冷冻工艺，使维生素、氨基酸、胶原蛋白、磷蛋白等营养素得以更好保存，同时采用复合酶技术和层析方法，使铅、汞、砷等有毒物质得以更好排除。、硬脂酸镁</p>\r\n<p>【标志性成分及含量】每100g含钙26g</p>\r\n<p>【保健功能】增加骨密度</p>\r\n<p>【适宜人群】中老年人</p>\r\n<p>【食用方法及食用量】每日2次，每次2粒</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403838868','1404510164','398','data/files/store_392/goods_5/small_201406271913254635.jpg','1','2595','2596','2604','0','200.00','100','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('329','392','material','玫瑰润洁晶体','<p align=\"left\">【产品成分】</p>\r\n<p align=\"left\">水果酵素、多醣体精华液、玫瑰精油、杏仁油、橄榄油、椰子油、蜂蜜、卵磷脂、荷荷巴油、可可油。</p>\r\n<p align=\"left\">【产品特点】</p>\r\n<p align=\"left\">保湿锁水，超干性肤质之润洁首选。高贵野生玫瑰萃取，超浓缩玫瑰精华加水果酵素具有丰富润滑成分，能滋润、修复并安抚肌肤，使肌肤润泽亮丽；独特维他命C+E净白配方，易渗透至深层肌肤，令肌肤摆脱黯沉、散发透白光采；富含多醣体精华，能够缓解细胞老化，抚平皱纹，焕白亮颜。大陆地区的春、冬季节尤为适用。。</p>\r\n<p align=\"left\">【注意事项】</p>\r\n<p align=\"left\">1.使用时，请勿将晶体直接冲水或浸泡于水，使用后请将晶体上的泡沫清除。</p>\r\n<p align=\"left\">2.使用后，请从晶体盒里取出垫片，将晶体置于垫片上，并放置于阴凉、通风、干燥处。</p>','2540','护肤彩妆	美容护肤	面部护理套装','','0','','','1','0',null,'1403838941','1404510145','399','data/files/store_392/goods_85/small_201406271914455852.jpg','1','2534','2535','2540','0','80.00','40','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('330','392','material','胶原蛋白水凝保湿面膜','<p align=\"left\">【产品特点】</p>\r\n<p align=\"left\">&middot;富含西洋参提取物和透明质酸，能深入角质层，紧贴皮肤，深层滋润肌肤，让肌肤饱满水润，持久保湿。另含独特胶原蛋白精华，刺激皮层新陈代谢，恢复细胞弹力，呈现自然光彩。</p>\r\n<p align=\"left\">&middot;丰富的维生素E精华，能增强肌肤的抗氧化性；同时具有天然持久的保湿效果，有效保持肌肤弹性，祛除细小皱纹，滋润呵护肌肤，令肌肤白里透红，保持青春活力。</p>\r\n<p align=\"left\">&middot;内含芦荟、海藻、甜叶菊等多种纯天然植物保湿成分，能增加肌肤水分含量，增强肌肤弹性，令肌肤倍感滋润；同时消除肌肤干燥紧绷感，绝不油腻，给肌肤24小时的持久保湿和舒适。</p>\r\n<p align=\"left\">&middot;纯天然绿茶精华，提升细胞的自体修复力，有效预防肌肤干纹、细纹的产生，重唤肌肤活力，延缓衰老。</p>\r\n<p>&nbsp;</p>\r\n<p>【主要成分】</p>\r\n<p>去离子水、甘油、甘油聚甲基丙烯酸酯、金缕梅提取物、西洋参提取物、扭刺仙人掌茎提取物、芦荟提取物、葡萄柚果提取物、薰衣草提取物、绿茶提取物、甜叶菊提取物、海藻糖、卡波姆、香精、水解胶原蛋白锌、透明质酸、甲基异噻唑啉酮、葡聚糖、生育酚（维生素E）、PEG-40氢化蓖麻油、三乙醇胺</p>\r\n<p>【使用方法】</p>\r\n<p>洁面后，取适量本品避开眼睛和嘴唇均匀涂于面部即可。建议每星期使用2到3次，用后可以免洗。</p>\r\n<p>【适用范围】适用于一般肤质</p>','2539','护肤彩妆	美容护肤	面膜','','0','','','1','0',null,'1403839036','1404510129','400','data/files/store_392/goods_155/small_201406271915558483.jpg','1','2534','2535','2539','0','80.00','60','');
