INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('339','392','material','软骨素氨糖胶囊','<p>【产品特点】</p>\r\n<p>金日牌软骨素氨糖胶囊是以盐酸氨基葡萄糖、硫酸软骨素、二氧化硅为主要原料制成的保健食品，经动物功能实验证明，具有增强免疫力的保健功能。</p>\r\n<p>&middot;氨糖与软骨素相互协同作用，促进人体吸收，缓解关节肿痛；</p>\r\n<p>&middot;氨糖与软骨素黄金比例完美搭配，养护、修复相辅相成，修复受损关节软骨；</p>\r\n<p>&middot;天然抗炎，抑制有害因子的生成与活性，有效抗炎、抗分解、抗衰老，提升免疫力，改善关节功能。</p>\r\n<p>【配料表】盐酸氨基葡萄糖、硫酸软骨素、二氧化硅</p>\r\n<p>【标志性成分及含量】每100g含：盐酸氨基葡萄糖63g、硫酸软骨素32g</p>\r\n<p>【保健功能】增强免疫力</p>\r\n<p>【适宜人群】免疫力低下者</p>\r\n<p>【不适宜人群】少年儿童、孕妇、乳母</p>\r\n<p>【食用方法及食用量】每日2次，每次2粒，口服</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403839983','1404509984','409','data/files/store_392/goods_105/small_201406271931451121.jpg','1','2595','2596','2604','0','480.00','200','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('340','392','material','西洋参保湿润肤露,','<p align=\"left\">&middot;蕴含丰富的西洋参精华、透明质酸和独特的胶原蛋白成分，可迅速到达皮肤深处，有效滋养干燥皮肤，修复受损细胞，恢复细胞弹力，使肌肤深层滋润，持久保湿。</p>\r\n<p align=\"left\">&middot;丰富的维生素E、维生素C及维生素原B5，加上甘油和天然芦荟精华，能迅速增加皮肤水分，令肌肤即时恢复弹性及润滑，保持肌肤年轻态。</p>\r\n<p align=\"left\">&middot;内含海藻、绿茶、甜叶菊等多种纯天然植物保湿成分，能增加肌肤水分含量，绝不油腻，保持肌肤24小时的滋润和舒适。</p>\r\n<p align=\"left\">&middot;含有小麦多肽和葡萄柚精华，对肌肤进行全面呵护。每日使用能为肌肤提供充足水分，长久保持柔润健康。</p>\r\n<p align=\"left\">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>【主要成分】</p>\r\n<p>去离子水、肉豆蔻酯异丙酯、矿油、天然脂肪醇、硬脂酸、PEG-20甲基葡萄糖苷倍半硬脂酸酯、甲基葡萄糖苷倍半硬脂酸酯、香精、西洋参提取物、扭刺仙人掌茎提取物、甘油、羟苯丙酯、牛油果树果脂、芦荟提取物、葡萄柚果提取物、薰衣草提取物、绿茶提取物、甜叶菊提取物、海藻糖、羟苯甲酯、小麦多肽、氢化橄榄油、氢化霍霍巴油、氢化葵花籽油、水解胶原蛋白锌、棕榈酸辛酯、泛醇（维生素原B5）、透明质酸、甲基异噻唑啉酮、葡聚糖、抗坏血酸（维生素C）、生育酚（维生素E）、三乙醇胺、二苯酮-4</p>\r\n<p>【使用方法】</p>\r\n<p>沐浴后，将本品均匀涂于全身肌肤，特别是手肘及足部，每天使用，能有效改善肌肤干燥现象。较干燥的部位，如双手、手肘及膝盖，建议每日多次涂抹。</p>\r\n<p>【适用范围】适用于一般肤质</p>','2536','护肤彩妆	美容护肤	身体护理','','0','','','1','0',null,'1403840193','1404509956','410','data/files/store_392/goods_126/small_201406271935266915.jpg','1','2534','2535','2536','0','42.00','30','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('341','392','material','丝然卫生湿巾,纯积分，仅需20分','<p align=\"left\">【产品特点】丝然卫生湿巾优选进口活性杀菌成分，对金黄色葡萄球菌、大肠杆菌有杀菌作用。含有润肤成分，不含酒精、香精，PH值接近人体正常皮肤。品质温和，服感舒适，清洁滋润。</p>\r\n<p align=\"left\">【主要成分】水刺无纺布、纯净水、聚一六甲基盐酸二胍、0.1%~0.2%（w/w）、尿囊素、甘油</p>\r\n<p align=\"left\">【使用方法】撕开包装袋取出湿巾，直接擦拭皮肤。</p>\r\n<p align=\"left\">【使用范围】本品适用于人体皮肤的卫生清洁，也可用于日常用品、卫生洁具表面的卫生清洁。</p>\r\n<p align=\"left\">【注意事项】避免高温及日光直射。</p>\r\n<p align=\"left\">【卫生许可证】粤卫消证字&#091;2009&#093;第0927号</p>\r\n<p align=\"left\">【执行标准】GB/T27728</p>\r\n<p align=\"left\">【卫生标准】GB15979</p>','1392','日用百货	洗护清洁	香薰用品','','0','','','1','0',null,'1403840268','1404509931','411','data/files/store_392/goods_8/small_201406271936486457.jpg','1','1337','1385','1392','0','0.00','20','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('342','392','material','仕女呵护晶体','<p align=\"left\">【产品成分】</p>\r\n<p align=\"left\">海藻萃取物、水果酵素、茶树精油、薄荷、荷荷巴油、蜂蜜、卵磷脂、乳酸、木瓜蛋白酶、凤梨蛋白酶、椰子油、甘油。</p>\r\n<p align=\"left\">【产品特点】</p>\r\n<p align=\"left\">1、有效抑制细菌滋生、保护肌肤卫生洁净，能紧缩女性私密处、消除私密处搔痒及异味、帮助改善阴部及跨下粗黑、暗沉颜色，使其洁净舒爽、细致粉嫩、恢复弹性；融合天然海藻萃取物，有效改善肌肤暗沉粗糙，自然呵护娇嫩肌肤，还您洁净舒爽。</p>\r\n<p align=\"left\">2、有效祛除痘痘，并改善湿疹、癣、瘙痒等问题肌肤。。</p>\r\n<p align=\"left\">【注意事项】</p>\r\n<p align=\"left\">1.使用时，请勿将晶体直接冲水或浸泡于水，使用后请将晶体上的泡沫清除。</p>\r\n<p align=\"left\">2.使用后，请从晶体盒里取出垫片，将晶体置于垫片上，并放置于阴凉、通风、干燥处。</p>','2536','护肤彩妆	美容护肤	身体护理','','0','','','1','0',null,'1403840337','1404509879','412','data/files/store_392/goods_82/small_201406271938021295.jpg','1','2534','2535','2536','0','78.00','40','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('343','392','material','舒达康胶囊,','<p>修复胃黏膜&nbsp; 增强胃动力</p>\r\n<p>【产品特点】</p>\r\n<p>宏雁牌舒达康胶囊是以蛋壳膜、白术提取物、白及提取物、淀粉为主要原料制成的保健食品，经动物功能和人体试食试验证明，具有对胃黏膜有辅助保护功能的保健功能。</p>\r\n<p><strong>舒达康胶囊作用机理</strong></p>\r\n<p><strong>营养胃粘膜细胞：</strong>为修复损伤的胃黏膜细胞提供重要的营养，促进胃黏膜细胞生长，加速胃黏膜修复。</p>\r\n<p><strong>在创面形成具有抗酸作用的生物膜：</strong>隔离胃酸、病菌及其他食物残渣对胃肠创面的侵蚀，暂时封闭创伤面，减少创面水分蒸发及被污染、感染的机会，使自然愈合过程不受干扰。</p>\r\n<p><strong>双向调节胃肠功能及粘膜屏障的防范：</strong>增强机体及胃的免疫力，增强胃动力，改善胃肠粘膜细胞代谢并促进胃溃疡、十二指肠溃疡的愈合。</p>\r\n<p>&nbsp;</p>\r\n<p>【主要原料】蛋壳膜、白术提取物、白及提取物、淀粉</p>\r\n<p>【标志性成分及含量】每100g含:氨基酸总量4.1g</p>\r\n<p>【保健功能】对胃粘膜有辅助保护功能</p>\r\n<p>【适宜人群】轻度胃黏膜损伤者</p>\r\n<p>【不适宜人群】少年儿童</p>\r\n<p>【食用方法及食用量】每日3次，每次3粒，饭前半小时食用</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403840427','1404509836','413','data/files/store_392/goods_164/small_201406271939243018.jpg','0','2595','2596','2604','0','200.00','150','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('344','392','material','365D健康活力杯','<p><strong>】</strong></p>\r\n<p>1、 去除水中氯化物，补充身体所需微量元素;</p>\r\n<p>2、 专有独家组方，双网设计，活化效果更快速;</p>\r\n<p>3、 活动滤芯设计，延长使用寿命，清洗、安装、拆卸更方便;</p>\r\n<p>4、 内外壁均选用食品级专用进口钢材，安全更长久；</p>\r\n<p><strong>【规格与执行标准】</strong></p>\r\n<p align=\"left\">规格：1个不锈钢杯+2个专用滤芯<strong></strong></p>\r\n<p align=\"left\">执行标准：QB/T 2332</p>\r\n<p align=\"left\"><strong>【使用方法】</strong></p>\r\n<p align=\"left\">1、适用水范围：符合日常生活直饮水标准的水。</p>\r\n<p align=\"left\">2、初始激活：使用前，将滤芯用60℃热水冲泡1次，以激活能量。</p>\r\n<p align=\"left\">3、使用方法：将水倒入杯中，需要上下摇晃。水温尽量不要高于50℃，以延长使用寿命。</p>\r\n<p align=\"left\">4、清洗方法：上滤芯：旋拧即可取下。下滤芯：请在柔软表面轻敲，请勿在硬物表面重敲。</p>\r\n<p align=\"left\">请定期（30一55天）对滤芯进行清洗，清洗方法：上、下滤芯取出后，直接浸入白醋30分钟左右后清洗干净即可。再在太阳下晒干。如长期停用，请清洗干净，风干存放。</p>\r\n<p align=\"left\">5、滤芯寿命：滤芯设计处理活化水量为3吨，为保证功效，建议根据使用次数和活化水量予以更换。</p>\r\n<p align=\"left\"><strong>【保养须知】</strong></p>\r\n<p align=\"left\">1、将上滤芯按顺时针方向，安装到活力杯杯盖内侧，柠紧；将下滤芯直接放入水杯底部；</p>\r\n<p align=\"left\">2、需要清洗时，上滤芯逆时针拧下，下滤芯请在柔软表面轻敲取出（请勿在硬物表面重敲，以免损伤杯口表面）;</p>\r\n<p align=\"left\">3、生产装配中，极个别杯内有细小物质漏出，不影响正常使用，清除即可。请勿吞食滤料；</p>\r\n<p align=\"left\">4、使用一段时间后，可能会有析出物，使水微微变混，属于正常现象，沉淀5分钟即可；</p>\r\n<p align=\"left\">5、不得用于糊状物质；</p>\r\n<p align=\"left\">6、浸泡茶叶会产生茶垢覆盖滤料，影响使用功能。可将水倒入水杯中存放1分钟，然后倒出泡茶或取出上、下滤芯泡茶；</p>\r\n<p align=\"left\">7、禁止放在微波炉或火上加热，严禁直接靠近火源；</p>\r\n<p align=\"left\">8、沸水放置水杯中后，杯盖拧紧放置时间建议不超过6小时，谨防产生负压，防止杯盖打不开；</p>\r\n<p align=\"left\">9、倒入沸水使用时，不宜过满，水离杯口3cm左右即可，避免拧紧杯盖时，导致水满溢出烫伤；</p>\r\n<p align=\"left\">10、为达到最佳保温效果，使用前先倒入少量热（冷）水，1-2分钟后倒出，正常使用情况下保温时间为6小时。</p>\r\n<p align=\"left\">11、为保证使用功能，不建议饮用放置在水杯超过24小时的水。</p>\r\n<p align=\"left\">12、杯身或者滤芯产生污渍，可用软毛刷浸沾白醋擦拭便可清除；</p>\r\n<p align=\"left\">13、空杯摇晃时有声音，属正常现象。</p>\r\n<p align=\"left\"><strong>【注意事项】</strong></p>\r\n<p>1、适宜人群：成人（孕妇、哺乳期妇女请在专业人士指导下使用）;</p>\r\n<p>2、初期使用者：每天饮水量从1杯开始逐渐增加到日常所需量，个别人会有轻微腹泻、眼睛发痒、喉咙发干、少量红疹或嗜睡等现象，均属正常的反应，一般1周即好；以上现象严重者，停用1一3天即将恢复正常；</p>\r\n<p>3、肾衰竭、心力衰竭、肝硬化等患者：开始应少量饮用，视身体情况逐渐增加饮用量；</p>\r\n<p>4、胃酸偏少、萎缩性胃炎者：建议减少饮用量；</p>\r\n<p>5、极个别过敏者请勿使用；</p>\r\n<p>6、服用西药者，服药前后半小时内不要饮用。</p>','2611','保健品	保健用品	磁水器','','0','','','1','0',null,'1403840569','1404509749','414','data/files/store_392/goods_46/small_201406271940468714.jpg','1','2595','2608','2611','0','280.00','120','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('345','392','material','铁锌咀嚼片','<p>科学补铁锌 健康不费心</p>\r\n<p>【产品特点】</p>\r\n<p>金日牌铁锌咀嚼片是以焦磷酸铁、柠檬酸锌等为主要原料制成的保健食品，具有补铁、补锌的保健功能。</p>\r\n<p><strong>铁锌的重要性</strong></p>\r\n<p><strong>铁</strong>是人体必需微量元素中含量最多的一种，总量为4-5克，主要存在血红蛋白中。缺铁容易导致贫血、抵抗力下降、疲劳、烦躁等不良症状。</p>\r\n<p><strong>锌</strong>是人体中必要的微量元素。人体内大约含有2g锌，大部分分布在骨骼、肌肉、血浆和头发中，含锌最高的组织是眼球的视觉部分（含4％）和前列腺。锌可以提高人体免疫系统的敏感性，同时可以直接抑制病毒的活性，从而增强人体抗病能力。缺锌容易导致食欲不振、生长停滞、味觉减退、创伤愈合不良的症状。</p>\r\n<p><strong>金日牌铁锌咀嚼片的配方特点</strong></p>\r\n<p>焦磷酸铁&mdash;&mdash;焦磷酸铁是儿童、中老年人、孕妇等广泛人群的食物铁营养强化剂和医药上补铁补血的最佳辅助产品。</p>\r\n<p>柠檬酸锌&mdash;&mdash;柠檬酸锌是存在人体母乳中唯一的一种锌配合物，比牛奶中的锌更易于吸收。</p>\r\n<p>酪蛋白磷酸肽&mdash;&mdash;酪蛋白磷酸肽可作为无机离子的载体促进肠膜对钙、镁、硒、锌等矿物质的吸收与利用，同时在提高机体免疫力、改善生殖性能等方面也有重要作用。</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】白砂糖粉、焦磷酸铁、柠檬酸锌、柠檬酸、酪蛋白磷酸肽、草莓香精、胭脂红</p>\r\n<p>【功效成份及含量】每片含：铁3.1mg、锌2.3mg</p>\r\n<p>【保健功能】补铁、补锌</p>\r\n<p>【适宜人群】需要补铁、补锌者</p>\r\n<p>【食用方法及食量】 每日3次，每次1片；嚼食</p>\r\n<p>【注意事项】&nbsp; 1、本品不能代替药物</p>\r\n<p>2、不宜超过推荐量或与同类营养品补充剂同时食用</p>','2605','保健品	保健食品	片剂','','0','','','1','0',null,'1403840642','1404509729','415','data/files/store_392/goods_186/small_201406271943066838.jpg','1','2595','2596','2605','0','90.00','50','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('346','392','material','特效浓缩卫浴清洁剂,','<p>【产品特点】</p>\r\n<p>生物洗涤、除菌、除臭、保养四效合一。有效去除卫浴间各种水垢、污渍、细菌。不损伤物体表面，保持洁具光亮如新、卫浴间清香自然，避免化学清洗的残留和污染。</p>\r\n<p>【主要成分】</p>\r\n<p>水、海洋生物、脂肪酸、柠檬酸、烷基糖苷、甲壳素、椰油酰胺丙基胺氧化物、生物酶、生理盐、食用香精等。</p>\r\n<p>【使用范围】</p>\r\n<p>浴缸、洗手盆、马桶、不锈钢、瓷砖、大理石、水磨石等各种洁具。</p>\r\n<p>【使用方法】</p>\r\n<p>将本品喷洒于物体表面数分钟后用毛刷刷洗，冲水擦净即可。</p>\r\n<p>【注意事项】</p>\r\n<p>污垢、油渍太厚时，先将其刮去再进行清洗。</p>','1392','日用百货	洗护清洁	香薰用品','','0','','','1','0',null,'1403840764','1404509712','416','data/files/store_392/goods_57/small_201406271944176999.jpg','1','1337','1385','1392','0','24.00','40','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('347','392','material','金日红正山小种','<p>金日红正山小种红茶是生长在武夷星村镇桐木村,座落在福建武夷山,国家级自然保护区内，总面积565平方公里，平均海拔700-1200米左右的山体下部或峡谷地带。那里气候温和，终日云雾弥漫，降雨量丰富，相对湿度大，冬暧夏凉，森林密布，生物种类有一万多种，被中外生物学家称之为是：&ldquo;绿色的翡翠，蛇的王国，鸟的天堂，昆虫的世界&rdquo;。</p>\r\n<p>生长在这种环境下的茶树不用喷洒农药，因为任何害虫都会被他们的天敌消灭。武夷山地质属于典型的丹霞地貌，矿物质和微量元素含量极其丰富。所以生长在这种环境下的金日红正山小种是名副其实的生态野茶。</p>\r\n<p>&nbsp;</p>\r\n<p>【品质特征】</p>\r\n<p>金日红正山小种条索肥壮，紧结园直，色泽乌光油润，泡水后汤色橙红，香气高长，茶汤浓醇无苦涩味，叶底厚实带古铜色，若加糖则更理想，因此适合欧洲国家加糖习俗，故其颇受英国、德国及北欧国家的青睐。</p>\r\n<p>&nbsp;</p>\r\n<p>【产品特点】</p>\r\n<p>金日红&ldquo;正山小种&rdquo;是一种天然的绿色饮品，长期科学饮用，有助于提神消疲、生津清热、利尿、抗氧化等。在作为一种解渴饮料的同时，又传播了茶文化，体现了品茶的高雅和品位。</p>','2597','保健品	保健食品	茶','','0','','','1','0',null,'1403840854','1404509695','417','data/files/store_392/goods_185/small_201406271946252442.jpg','1','2595','2596','2597','0','400.00','200','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('348','392','material','特效浓缩全能洗宝','<p>1、能充分分解蔬菜、瓜果上的残留农药等公害污染物，细心呵护您的健康。</p>\r\n<p>2、天然生物精华及可食用原料生产，不含有害成分，洗后蔬果、碗碟可放心使用。</p>\r\n<p>3、低泡易冲洗，省水、省力。</p>\r\n<p>4、迅速分解油污，使洗过的碗碟清洁怡人。</p>\r\n<p>5、纯正温和，倍加体贴双手。</p>\r\n<p>【主要成分】</p>\r\n<p>去离子水、海洋生物、生物碱、烷基糖苷、超强生物酶、食用香精等。</p>\r\n<p>【使用范围】</p>\r\n<p>瓜果、蔬菜；餐具、茶具等餐饮物品。</p>\r\n<p>【使用方法】</p>\r\n<p>1、碗碟等厨房用具，以1：3或者1：4的比例稀释，将稀释液挤于海绵或洗碗布清洗碗碟，然后以清水冲洗干净。</p>\r\n<p>2、瓜果蔬菜等，以1：8的比例稀释清洗，或在每升清水中加入1毫升特效浓缩液，洗涤后用清水冲洗干净。</p>\r\n<p>3、清洁地板、墙壁、塑料制品等家居用品，以1：4或者1：5的比例稀释，洗涤后用清水洗净。</p>\r\n<p>4、清洗玻璃、木制家具等家居用品，以1：8的比例稀释，洗涤后用清水洗净。</p>\r\n<p>5、洗车，用1：10的比例稀释，洗涤后用清水洗净。</p>\r\n<p>【注意事项】</p>\r\n<p>1、若使用稀释瓶稀释原液，请在3个月内用完，若有沉淀或悬浮物属于正常现象，请摇匀后使用。</p>\r\n<p>2、本产品不含荧光增白剂、苯、磷等石化成分，无毒、无刺激，对人体无害。为非饮用品。</p>','1392','日用百货	洗护清洁	香薰用品','','0','','','1','0',null,'1403840964','1404509675','418','data/files/store_392/goods_72/small_201406271947526671.jpg','1','1337','1385','1392','0','28.00','20','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('349','392','material','心源素胶囊','<p>心好，一切都好</p>\r\n<p>【产品特点】</p>\r\n<p>金日牌心源素胶囊是以西洋参、三七、五味子、维生素E为主要原料制成的保健食品，经功能试验证明，具有耐缺氧和延缓衰老的保健功能。</p>\r\n<p>选择金日心源素的5 大理由</p>\r\n<p>1、 著名品牌，权威研制</p>\r\n<p>组织国内外多位心脑健康专家，历经3年的时间研制成功；让您的心脑血管更健康<strong>。</strong></p>\r\n<p>2、配伍独特，效果显著</p>\r\n<p>3、先清后补，心脑健康</p>\r\n<p>创造传统心脑保健养生文化+西方医疗保健概念=&ldquo;先清后补&rdquo;的心脑保健理念</p>\r\n<p>清&mdash;&mdash;三七活血化瘀通络，清洁血液；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 维生素E清除自由基，清除血管硬化斑块。</p>\r\n<p>补&mdash;&mdash;西洋参滋阴补气，充盈血氧，促进人体血氧供&nbsp; 应，强化心脑功能；</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 五味子补肾滋阴，养心安神。</p>\r\n<p>4、市场火爆，口碑佳</p>\r\n<p>历经10余年的市场考验，上市以来，受到全国各地广大消费者的青睐，收到消费者感谢信无数。</p>\r\n<p>5、国家专利，有保证</p>\r\n<p>2004年5月荣获国家知识产权局颁发的发明专利（专利号【ZL 99118777.6】）。</p>\r\n<p>&nbsp;</p>\r\n<p>【配料表】西洋参、三七、五味子、维生素E<br />【功效成份及含量】每100g中含人参皂甙&ge;7g</p>\r\n<p>【保健功能&#093;】耐缺氧、延缓衰老<br />【适宜人群】处于缺氧环境者、中老年人</p>\r\n<p>【不适宜人群】少年儿童、孕妇<br />【食用方法及食用量】每日2-3次，每次2粒，饭后口服</p>','2604','保健品	保健食品	胶囊','','0','','','1','0',null,'1403841039','1404509656','419','data/files/store_392/goods_183/small_201406271949438334.jpg','1','2595','2596','2604','0','200.00','140','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('350','392','material','特效浓缩洗衣液','<p>【产品特点】</p>\r\n<p>去污功效强、低泡易过水、除菌抗静电，清爽柔和，气味芬芳，不伤衣物和皮肤，多种特效生物酶可瞬间去除衣物上的顽固污渍，使洗涤后的衣物更洁净、更蓬松柔软、倍感润滑舒适。</p>\r\n<p>【主要成分】</p>\r\n<p>去离子水、海洋生物、非离子表面活性剂、阴离子表面活性剂、超强蛋白酶、脂肪酶、安全抗菌剂、椰子油衍生物、甲壳素、生物酶、生理盐、食用香精等。</p>\r\n<p>【使用范围】</p>\r\n<p>适用于棉、毛、麻、混纺、合成纤维等各类衣物，同时更适合内衣、内裤及婴幼儿衣物的清洗。</p>\r\n<p>【使用方法】</p>\r\n<p>1、预洗，先将洗衣液直接涂在衣服的污迹上，5分钟后放入洗衣机清洗或手洗。</p>\r\n<p>2、机洗，每5升水中加入10毫升（1瓶盖）洗衣液，若用于大型洗衣机，10升水可加</p>\r\n<p>入洗衣液25毫升（两瓶盖半）。</p>\r\n<p>3、手洗，10升水可加入洗衣液10毫升（一瓶盖）。</p>\r\n<p>【注意事项】</p>\r\n<p>1、本品可当衣领净使用，严重污渍处如衣领、袖口等可将本品直接涂在污渍上停留5分钟左右，再浸泡洗涤。</p>\r\n<p>2、普通衣服长时间（1-3小时）浸泡可自动分解污渍，过水即净。</p>\r\n<p>3、请勿长时间预浸或以原液预洗真丝或羊毛衣料。</p>\r\n<p>4、若使用稀释瓶稀释原液，请在3个月内使用完，若有沉淀或悬浮物，属于正常现象，请摇均匀后使用。</p>','1397','日用百货	洗护清洁	洗衣液','','0','','','1','0',null,'1403841114','1404509629','420','data/files/store_392/goods_57/small_201406271950579275.jpg','1','1337','1385','1397','0','36.00','24','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('351','392','material','洁适雅宜早宜晚牙膏','<p><strong>美好生活，从&ldquo;齿&rdquo;开始</strong></p>\r\n<p>白天刷牙，重在清洁、美白牙齿与保持口气清新；<br />晚上刷牙，重在有效清除食物残渣、抑菌、防蛀。<br />洁适雅宜早宜晚牙膏早晚全面立体护理，早间功效护理+晚间长效巩固，让产品功能真正有效。<br />&nbsp;<br /><strong>洁适雅宜早牙膏（绿茶香型）<br /></strong>【产品特点】<br />&bull;蕴含洋甘菊、西洋参等草本精华成分；<br />&bull;针对白天口腔环境，有效清洁牙齿，有助于减少齿垢及牙菌斑；<br />&bull;冰爽绿茶口味，口气持久清新；<br />&bull;温和配方无刺激，有助舒缓牙齿、牙龈敏感。<br />【产品成分】<br />山梨（糖）醇、水合硅石、纯净水、甘油、聚乙二醇400、月桂醇硫酸酯钠、香精、纤维素胶、皱波角叉菜、钛白粉（CI77891）、氯化锶、洋甘菊提取物、西洋参根提取物、糖精钠、西吡氯铵、磷酸三钠、羟苯甲酯、羟苯丙酯、CI74260<br />【净含量】150g</p>\r\n<p>&nbsp;</p>\r\n<p><strong>洁适雅宜晚牙膏（留兰香型）<br /></strong>【产品特点】<br />&bull;蕴含蜂胶、西洋参等精华成分，有助抑制多种口腔细菌；<br />&bull;针对夜间口腔环境设计，香气清雅如兰，适合夜间温馨睡眠；<br />&bull;含氟化物，可帮助强健牙齿，防止蛀牙；<br />&bull;温和配方无刺激，营养牙龈，保护牙釉质。<br />【产品成分】<br />磷酸氢钙二水合物、纯净水、山梨(糖)醇、甘油、水合硅石、月桂醇硫酸酯钠、香精、单氟磷酸钠（0.1%，以氟计）、纤维素胶、皱波角叉菜、羟乙基纤维素、蜂胶提取物、西洋参根提取物、糖精钠、焦磷酸四钠、苯甲酸钠<br />【净含量】170g</p>\r\n<p>使用方法： <br />1、养成&ldquo;饭后漱口，早晚刷牙&rdquo;的习惯，每天早晚用洁适雅TM牙膏刷牙；<br />2、刷牙时用软毛牙刷，挤2cm左右牙膏于牙刷上，顺着牙缝上牙从上往下刷，下牙从下往上刷，用力均匀不能过猛，避免损伤牙龈及牙齿。刷完后，漱净口中残余牙膏；<br />3、每次刷牙牙膏泡沫在口腔中停留的时间不少于3分钟。</p>\r\n<p><strong>温馨提示：现有旅行装同步上线，规格为：30g+33g/盒&nbsp;&nbsp; 全国统一零售价：16元/盒<br /></strong></p>','1388','日用百货	洗护清洁	牙膏','','0','','','1','0',null,'1403841239','1404509614','421','data/files/store_392/goods_163/small_201406271952431552.jpg','1','1337','1385','1388','0','36.00','20','');
