INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('353','392','material','特效浓缩厨房油烟净','<p>【产品特点】</p>\r\n<p>1、迅速分解油污，不伤害物品，不伤人体。</p>\r\n<p>2、除臭、除腥味、迅速消除厨房各种异味，抗菌除菌，确保厨房环境卫生。</p>\r\n<p>3、超强去污，洗后无残留。</p>\r\n<p>4、省事省力，无需过水，令物品立即清洁如新，光彩再现。</p>\r\n<p>【主要成分】</p>\r\n<p>去离子水、海洋生物、非离子表面活性剂、阴离子表面活性剂、生物碱、柠檬酸、烷基糖苷、超强生物酶、生理盐、食用香精等。</p>\r\n<p>【使用范围】</p>\r\n<p>用于清洗油烟机、排气扇、炉灶、瓷砖、厨房等表面的油污、汤汁污渍。</p>\r\n<p>【使用方法】</p>\r\n<p>1、本品为特效浓缩液，配比为1U3或1U4。</p>\r\n<p>2、直接均匀喷在物品表面，片刻后，用布擦去油污，形成保护膜。</p>\r\n<p>3、遇顽固油污时，用百洁布沾本品直接涂擦或先用报纸沾油烟机表面，再将本品喷于报纸上，停留5-10分钟，即可擦净。（油污过厚时，可将其刮去再喷上本产品以加速清洗）</p>\r\n<p>4、清洗油烟机内部，无须拆卸，先将本品均匀喷在扇叶上，片刻后，再开动油烟机，油污即自动脱落流进油盒，积油尽除。</p>\r\n<p>【注意事项】</p>\r\n<p>1、若使用稀释瓶稀释原液，请在3个月内用完，若有沉淀或悬浮物属于正常现象，请摇匀后使用。</p>\r\n<p>2、本产品使用纯天然原料，对人体无伤害。建议每月清洗厨房不少于2次。</p>','1392','日用百货	洗护清洁	香薰用品','','0','','','1','0',null,'1403841411','1404509593','423','data/files/store_392/goods_143/small_201406271955433642.jpg','1','1337','1385','1392','0','22.00','40','');
INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('354','392','material','掌灸纳米精油','<p><strong>【产品特点】</strong></p>\r\n<p>使用简便、安全、无副作用；</p>\r\n<p>方便携带，随时随地消除各类疼痛；</p>\r\n<p>无色无毒无异味，纳米技术，见效快；</p>\r\n<p>不沾染衣物，无残留物，干净卫生。</p>\r\n<p><strong>【主要成份】</strong></p>\r\n<p>藏红花提取物，路路通提取物，干姜精油，薄荷精油，龙脑，三七提取物，桂枝提取物，玫瑰精油，茉莉精油。</p>\r\n<p><strong>【主要作用】</strong></p>\r\n<p>舒缓肌肤组织，缓解肌体疲劳和运动障碍，改善肌肤微血管循环，淡化皮肤黑色素。</p>\r\n<p><strong>【使用方法】</strong></p>\r\n<p>将本品0.05毫升左右涂于掌心或相关穴位，用掌心迅速捂住患处，持续热效15秒以上，待灼热适度时松手即可，每日3至5次为佳。</p>','1356','日用百货	居家日用	创意生活','','0','','','1','0',null,'1403841500','1404509551','424','data/files/store_392/goods_25/small_201406271957053570.jpg','1','1337','1355','1356','0','180.00','180','');
