INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('547','275','material','三九九港 海狗人参丸 软胶囊','<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">补肾第一佳品&rdquo;&mdash;&mdash;海狗是生活在加拿大北极圈</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">内的深海哺乳动物，被称为北极瑰宝。海狗肌</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">体含有八种人体必需但自身不能合成的氨基</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">酸；血红蛋白含量是牛肉的20倍；含铁量是鱼</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">类的100倍；含磷量是鱼类的2倍；含锌量是鱼</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">类的4倍；其他微量元素含量是鱼类的50倍</span><span style=\"font-size: 36px;\">，</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">且所含微量元素均为有机结合形式，易为人体</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">吸收利。人参富含人参皂甙和人参多糖，能提</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">高血液中的氧气载体&ldquo;红细胞&rdquo;的寿命和存活</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">率，增强人体内各类组织细胞对氧及其他养分</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">的吸收。多数海洋生物为低级冷血动物，而海</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">狗是难得的海洋热血哺乳动物，其性平、味咸，入肝肾经，具有既滋阴又补阳的功效。科</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">学研究发现，雄海狗之所以拥有如此强劲的体</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">能，是由于其生殖系统中的生殖细胞转录因子</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">及不饱和脂肪酸等生殖活性物质含量极其丰</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">富，能够最大程度的调整睾丸酮分泌，促使机</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">体自我强阳，能够不断自我修复和自我补给</span><span style=\"font-size: 36px;\">。</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">因此，海狗被国际医学界视为一种有相当价值</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">的珍贵滋补营养品资源，被科学家誉为&ldquo;海洋性</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 36px;\">宝&rdquo;！</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"color: #ff0000;\"><strong style=\"font-size: 36px;\"><span style=\"line-height: 1.5;\"><strong><span style=\"color: #ff0000; font-size: xx-large;\"><img style=\"vertical-align: top;\" src=\"http://gd1.alicdn.com/imgextra/i1/1969032656/T2UEPQXy8XXXXXXXXX_!!1969032656.jpg\" alt=\"\" align=\"absmiddle\" /><img style=\"vertical-align: top;\" src=\"http://gd2.alicdn.com/imgextra/i2/1969032656/T2BCTPXuXaXXXXXXXX_!!1969032656.jpg\" alt=\"\" align=\"absmiddle\" /><img style=\"vertical-align: top;\" src=\"http://gd3.alicdn.com/imgextra/i3/1969032656/T2oUYRXp0XXXXXXXXX_!!1969032656.jpg\" alt=\"\" align=\"absmiddle\" /><img style=\"vertical-align: top;\" src=\"http://gd3.alicdn.com/imgextra/i3/1969032656/T2_APPXvBaXXXXXXXX_!!1969032656.jpg\" alt=\"\" align=\"absmiddle\" /><img style=\"vertical-align: top;\" src=\"http://gd4.alicdn.com/imgextra/i4/1969032656/T2VCHPXudaXXXXXXXX_!!1969032656.jpg\" alt=\"\" align=\"absmiddle\" /></span></strong></span></strong></span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; text-indent: 28pt;\"><span style=\"background-color: #fcfcfc; font-family: 宋体; font-size: 14pt;\"><img style=\"vertical-align: top;\" src=\"http://gd3.alicdn.com/imgextra/i3/1969032656/T22HTRXxNXXXXXXXXX_!!1969032656.jpg\" alt=\"\" width=\"785\" height=\"443\" align=\"absmiddle\" /><img style=\"vertical-align: top;\" src=\"http://gd4.alicdn.com/imgextra/i4/1969032656/T2duDRXwlXXXXXXXXX_!!1969032656.jpg\" alt=\"\" width=\"789\" height=\"508\" align=\"absmiddle\" /><img style=\"vertical-align: top;\" src=\"http://gd2.alicdn.com/imgextra/i2/1969032656/T2Q6bRXtBXXXXXXXXX_!!1969032656.jpg\" alt=\"\" width=\"746\" height=\"337\" align=\"absmiddle\" /></span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; text-indent: 28pt;\"><span style=\"background-color: #fcfcfc; font-family: 宋体; font-size: 14pt;\">源生堂海狗人参丸是以纳米比亚南海狗精为原料、采用现代最先进的超微过滤技术、严格按GMP标准精炼而成。对增强男性性功能，具有极强的效果！海狗体内含丰富的肾活力因子（维持人体良好精神状态的重要物质）；血红蛋白含量丰富，补血益气；所含铁、钙、镁、锶、锌、硒及维生素B1、维生素B2等丰富的微量元素及维生素，可调节人体生理平衡，补充人体无机盐；海狗体内所含17种氨基酸可改善新陈代谢、补充蛋白质、提供能量；海狗体内丰富的胶原蛋白、粘性蛋白、多肽、血色素、核酸等活性物质，可激发细胞活力、平衡内分泌。</span><span style=\"font-family: 宋体; font-size: 14pt;\">海狗人参丸补益精气，缓解疲劳，对虚损精气补充迅速充足，提升机体能力。对自汗、盗汗、肾虚阳痿、遗精、腰膝酸痛、病后体弱、补肾、抗疲劳、增强机体免疫力有一定的作用。补肾壮阳、保精固肾、阳痿早泄有极好的改善作用；有效预防和改善中老年生理疲劳，生理功能退化；补充元气，改善神疲乏力、腰酸背痛、预防过早衰老，增强体力；改善身体虚弱、精力差、免疫力低下、易生病。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; text-indent: 21pt;\"><span style=\"background-color: #fcfcfc; font-family: 宋体; font-size: 14pt;\">源生堂</span><span style=\"font-family: 宋体; font-size: 14pt;\">海狗人参丸，以中医补气培元理论，对各种男性疾病有非常好的改善及预防作用。中药成分，无毒副作用，不透支身体，不疲劳，效果好。海狗精、人参更是国际公认的安全有效成分。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><strong><span style=\"color: red; font-family: 宋体; font-size: 18pt;\">海狗人参丸成份介绍：</span></strong></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; text-indent: 21pt;\"><span style=\"font-family: 宋体; font-size: 14pt;\">海狗精粉：壮阳滋阴、补肾益肝、调节内分泌。补血益气、强筋壮骨。提高性功能和免疫力，治阳痿、早泄、神经衰弱等。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 枸杞子：补肾益精，养肝明目，补血安神，生津止渴，润肺止咳。治肝肾阴亏，腰膝酸软，头晕，目眩，目昏多泪，虚劳咳嗽，消渴，遗精。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 人参：大补元气，复脉固脱，安神益智。治劳伤虚损、眩晕头痛、阳痿、尿频、妇女崩漏、一切气血津液不足之症。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 山药：清热，解毒。治温病发热，热毒血痢，痈疡，肿毒，瘰疬，痔漏。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 淫羊藿：补肾阳，强筋骨，祛风湿。用于阳痿遗精，筋骨痿软，风湿痹痛，麻木拘挛；更年期高血压。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 肉桂：补元阳，暖脾胃，除积冷，通血脉。治命门火衰，肢冷脉微，亡阳虚脱，腹痛泄泻，虚阳浮越。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 杜仲：补肝肾，强筋骨，安胎。治腰脊酸疼，足膝痿弱，小便余沥，阴下湿痒，胎漏欲堕，胎动不安，高血压。</span><br /><span style=\"font-family: 宋体; font-size: 14pt;\"> 食用明胶：别名黄明胶，以牛皮等提制的胶质入药，止血。主治吐血，衄血，便血，血崩等症。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><strong><span style=\"color: red; font-family: 宋体; font-size: 18pt;\">需服用海狗人参丸的男士：</span></strong></p>\r\n<p style=\"margin: 0cm 0cm 0pt 15.75pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-family: 宋体; font-size: 14pt;\">1.</span><span style=\"font-family: 宋体; font-size: 14pt;\">欲延长性生活时间、次数、频率，提高性商、性能力的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />2.</span><span style=\"font-family: 宋体; font-size: 14pt;\">做爱时间少于30分钟的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />3.</span><span style=\"font-family: 宋体; font-size: 14pt;\">阳痿、早泄、性功能障碍的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />4.</span><span style=\"font-family: 宋体; font-size: 14pt;\">患前列腺疾病的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />5.</span><span style=\"font-family: 宋体; font-size: 14pt;\">举而不坚，硬度不够的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />6.</span><span style=\"font-family: 宋体; font-size: 14pt;\">阴囊潮湿、遗精、少精的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />7.</span><span style=\"font-family: 宋体; font-size: 14pt;\">性功能减退、对性刺激不敏感的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />8.</span><span style=\"font-family: 宋体; font-size: 14pt;\">做爱过程中出现中途疲软的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />9.</span><span style=\"font-family: 宋体; font-size: 14pt;\">房事后疲劳，腰膝酸软、四肢发抖者；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />10.</span><span style=\"font-family: 宋体; font-size: 14pt;\">阴茎勃起无力、射精强度弱的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />11.</span><span style=\"font-family: 宋体; font-size: 14pt;\">频繁手淫，或者纵欲过度的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />12.</span><span style=\"font-family: 宋体; font-size: 14pt;\">渴望和谐性爱的中老年男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />13.</span><span style=\"font-family: 宋体; font-size: 14pt;\">吃西药怕副作用伤害身体的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />14.</span><span style=\"font-family: 宋体; font-size: 14pt;\">长期服用补肾药效果不明显的男性；</span><span style=\"font-family: 宋体; font-size: 14pt;\"><br />15.</span><span style=\"font-family: 宋体; font-size: 14pt;\">阴茎短小，无法满足爱人的男性。</span></p>\r\n<p style=\"margin: 0cm 0cm 0pt 15.75pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><strong><span style=\"background-color: #fcfcfc; color: red; font-family: 宋体; font-size: 18pt; background-position: initial initial; background-repeat: initial initial;\">三九集团养生食品 纯天然 易吸收 更健康</span></strong></p>\r\n<ul style=\"margin-top: 1em; margin-right: 1em; margin-bottom: 1em; padding-left: 40px; list-style-position: initial; list-style-image: initial; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\">\r\n<li style=\"list-style: square;\"><strong><span style=\"background-color: #fcfcfc; color: red; font-family: 宋体; font-size: 18pt; background-position: initial initial; background-repeat: initi