INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('647','506','material','保健背心','<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><strong style=\"line-height: 1.5; color: #9900ff;\"><span style=\"font-size: 18px;\"><span style=\"color: #000000;\">【产品名称】：<span style=\"color: #9900ff;\">中国</span></span>天马服饰远红外甲壳素保健男女情侣背心</span></strong></p>\r\n<p style=\"margin: 13.45pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: #404040;\"><span style=\"color: #9900ff;\"><strong><span style=\"font-size: 18px;\"><span style=\"color: #000000;\">【产品成分】：</span>微元生化纤维，甲壳素，罗布麻，棉麻。</span></strong></span></p>\r\n<p style=\"margin: 13.45pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: #404040;\"><strong><span style=\"font-size: 18px;\">【产 &nbsp; &nbsp; &nbsp; 地】：<span style=\"color: #9900ff;\">湖北省纺织第一镇&mdash;&mdash;汉川市马口镇</span></span></strong></p>\r\n<p style=\"margin: 13.45pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: #404040;\"><strong></strong><strong><span style=\"font-family: lisu;\"><span style=\"font-size: 18px;\"><span style=\"color: #000000;\">【适用范围】：</span></span></span></strong><span style=\"color: #9900ff;\"><strong><span style=\"font-family: lisu;\"><span style=\"font-size: 18px;\">适用于高血压、高血脂，缓解上半身骨关节疾病及</span></span></strong></span><strong style=\"color: #9900ff;\"><span style=\"font-family: lisu;\"><span style=\"font-size: 18px;\">风湿类风湿关节炎、骨质增生</span></span></strong><strong style=\"color: #9900ff;\"><span style=\"font-family: lisu;\"><span style=\"font-size: 18px;\">而引起的疼痛，提高各相应关节的活动力度</span></span></strong><strong style=\"color: #9900ff;\"><span style=\"font-family: lisu;\"><span style=\"font-size: 18px;\">，慢性支气管炎、哮喘、胃肠疾病以及女性乳房亚健康疾病等症状。</span></span></strong></p>\r\n<p style=\"margin: 13.45pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: #404040;\"><span style=\"color: #000000;\"><strong></strong></span><strong><span style=\"font-size: 24px;\"><span style=\"color: #0000ff;\">【颜色选择】：女式</span></span></strong><strong style=\"line-height: 1.5;\"><span style=\"color: #9900ff;\"><span style=\"font-size: 18px; line-height: 1.5;\">颜色是随机发货的哦,但尽量按顾客的意愿发货)。默认圆通快递（其它快递补差价）<span style=\"color: #ff0000;\"><span style=\"font-family: lisu;\"><span style=\"font-size: 24px;\">（港 澳 台邮费另议</span></span></span></span></span><span style=\"font-family: lisu;\"><span style=\"font-size: 24px;\"><span style=\"color: #ff0000;\"><span style=\"line-height: 1.5;\">&nbsp;</span><strong style=\"line-height: 1.5;\"><span style=\"line-height: 1.5;\">）</span></strong></span></span></span></strong></p>\r\n<p style=\"margin: 13.45pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: #404040;\"><span style=\"color: #000000;\"><strong><span style=\"font-size: 18px;\"><strong style=\"line-height: 1.5;\"></strong>【产品功效】：</span></strong></span><span style=\"line-height: 20pt; text-indent: 30pt; font-size: 15pt; font-family: 华文中宋; color: #c00000;\">天马保健服饰是采用高科技科研成果与传统的中医药技术相结合，开发生产的高科技产品，其中有世界发明金奖、国家级发明金奖、国家专利产品。该产品利用</span><span style=\"line-height: 20pt; text-indent: 30pt; font-size: 15pt; font-family: 华文中宋; color: #e36c0a;\">高科技技术</span><span style=\"line-height: 20pt; text-indent: 30pt; font-size: 15pt; font-family: 华文中宋; color: black;\">把甲壳素和微元生化纤维</span><span style=\"line-height: 20pt; text-indent: 30pt; font-size: 15pt; font-family: 华文中宋; color: #943634;\">添加到衣服上，</span><span style=\"line-height: 20pt; text-indent: 30pt; font-size: 15pt; font-family: 华文中宋; color: #c00000;\">具有增强免疫、调节血脂、调节血糖、调节血压、排除体内重金属、排除有毒有害物质、延缓衰老、抗疲劳、抗辐射、抑制肿瘤细胞、减肥、美容等保健作用。代替了刮痧、拨火罐、按摩、针灸、足疗、化疗、电疗、美容等耗时费力的理疗方式，调理人体因为微循环障碍引起的各种慢性疾病，如乳腺癌、子宫肌瘤、脂肪瘤、脑血栓、前列腺炎、类风湿、高血压、糖尿病、心脏病、冠心病、颈椎病、肩周炎、皮肤病、胃病、肝病&hellip;&hellip;，产品理疗效果显著。</span></p>\r\n<p style=\"margin: 12pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; text-indent: 24pt; line-height: 20pt;\" align=\"left\"><span style=\"font-size: 15pt; font-family: 华文中宋;\">1.&nbsp;</span><span style=\"font-size: 15pt; font-family: 华文中宋;\">甲壳素（几丁聚糖）：<span style=\"color: #c00000;\">是天然物质，它可以吸湿，吸附重金属。具有调节免疫，抗菌，防臭、预防皮肤过敏等作用，还具有天然的抗菌活性，抗菌普遍，对革兰氏阳性菌、革兰氏阴性菌及念珠菌均有明显的抑杀效果。甲壳素本身带正电，而细菌的细胞膜带负电，当细菌接触到甲壳素抗菌衣，因为正负相吸，甲壳素会对细菌产生穿刺效果，扯破细菌的细胞膜，导致细菌死亡，所以说甲壳素可以抑制细菌生长。它对人体有</span>五大功能：<span style=\"color: #0070c0;\">一是</span><span style=\"color: #c00000;\">免疫强化机能，</span><span style=\"color: #0070c0;\">二是</span><span style=\"color: #c00000;\">抑制细胞老化，</span><span style=\"color: #0070c0;\">三是</span><span style=\"color: #c00000;\">预防疾病产生，</span><span style=\"color: #0070c0;\">四是</span><span style=\"color: #c00000;\">促进疾病痊愈；</span><span style=\"color: #0070c0;\">五是</span><span style=\"color: #c00000;\">调节生理机能。甲壳素对人体疾病的预防和保健有</span>九大作用：<span style=\"color: #0070c0;\">(</span><span style=\"color: #0070c0;\">一)</span><span style=\"color: #c00000;\">强化免疫功能，对癌症有抑制作用；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">二)</span><span style=\"color: #c00000;\">降低胆固醇；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">三)</span><span style=\"color: #c00000;\">降血压作用；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">四)</span><span style=\"color: #c00000;\">降血糖作用；</span><span style=\"color: #00b0f0;\">（五）</span><span style=\"color: #c00000;\">强化肝脏机能；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">六)</span><span style=\"color: #c00000;\">对神经、内分泌系统的调节作用；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">七</span><span style=\"color: #c00000;\">)</span><span style=\"color: #c00000;\">改善腰酸背痛；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">八)</span><span style=\"color: #c00000;\">治疗烧伤、烫伤，加速外伤愈合；(九)吸附体内有害物质并排除于体外；</span><span style=\"color: #00b0f0;\">(</span><span style=\"color: #00b0f0;\">九)</span><span style=\"color: #c00000;\">吸附体内有害物质并排除于体外。具体而言，它具有调节免疫、调节血脂、调节血糖、调节血压、排除体内重金属、排除有毒有害物质、延缓衰老、抗疲劳、抗辐射、抑制肿瘤、减肥、美容等保健作用。</span></span></p>\r\n<p style=\"margin: 12pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; text-indent: 24pt; line-height: 20pt;\" align=\"left\"><span style=\"font-size: 15pt; font-family: 华文中宋;\">2</span><span style=\"font-size: 15pt; font-family: 华文中宋;\">．微元生化纤维（远红外纤维M、B、P）：</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #c00000;\">是指能持久的发射人体吸收的远红外波，消化人体细胞，改善人体微循环，能有效的消除肿块，具有抗菌保健的功效。它含有30多种微量元素，采用铝、硅、钛、锆等元素与聚酯混纺制成各种服饰，微元生化纤维与人体肌肤接触后能在短时间内激发人体生物微元活化，从而使人体产生特殊的生理效应，促进血液循环和新陈代谢，加速血乳酸分解，消除肌肉疼痛及疲劳，达到防病强身功能。具体而言，</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #00b0f0;\">一是</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #c00000;\">它能把人体产生的远红外线成45角折射到人体皮肤，改善人体微循环；</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #00b0f0;\">二是</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #c00000;\">可以吸收人体周围的能量，通过热辐射，作用于人体皮肤，改善微循环，促进血液流通，使各个器官得到均衡营养，保持人体正常新陈代谢；</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #00b0f0;\">三是</span><span style=\"font-size: 15pt; font-family: 华文中宋; color: #c00000;\">可以吸收人体排泄的废物，对高血压、糖尿病、冠心病、胃病、肝病、中风、风湿病、癌症、头痛、关节痛等疾病具有预防和辅助疗效，对肥胖人有瘦身减肥作用。它比一般纺织品的保暖率高38%，它的功效与衣物同在，十年的衰退率为10%。</span></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px;\"><span style=\"font-size: 15pt; font-family: 华文中宋; color: #c00000;\">&nbsp; &nbsp; &nbsp; 甲壳素加上微元生化纤维的双重功效，最终达到预防和治疗疾病、强身健体的功效</span>。<strong style=\"color: #404040; line-height: 1.5;\"><span style=\"font-size: 18px;\"><span style=\"color: #9900ff;\"><span style=\"font-family: 宋体;\">天马服饰之所以有这么</span></span><span style=\"color: red; font-family: kaiti_gb2312;\">&ldquo;</span><span style=\"color: red;\"><span style=\"font-family: 宋体;\">神奇</span></span><span style=\"color: red; font-family: kaiti_gb2312;\">&rdquo;</span><span style=\"font-family: 宋体;\"><span style=\"color: red;\">的治疗</span><span style=\"color: black;\">功效就在于：</span><span style=\"color: red;\">微元生化纤维</span></span><span style=\"color: red; font-family: kaiti_gb2312;\">+</span><span style=\"font-family: 宋体;\"><span style=\"color: red;\">甲壳素混合棉麻编织</span><span style=\"color: black;\">而成，微元生化纤维能快速</span><span style=\"color: red;\">促进微循环</span><span style=\"color: black;\">，</span><span style=\"color: blue;\">渗透皮肤3&mdash;5厘米，</span><span style=\"color: black;\">促进血液及其他津液的流通，甲壳素</span><span style=\"color: red;\">调节免疫，血脂、血糖、血压</span><span style=\"color: black;\">，延缓衰老，抗疲劳辐射，减肥美容，</span><span style=\"color: red;\">两者协同合作，微元生化纤维促进微循环，疏通血管排出毒素，甲壳素活化代谢细胞，达到预防疾病和调理疾病的功效。</span></span></span></strong></p>\r\n<p style=\"margin: 13.45pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; line-height: 21px; color: #404040;\"><span style=\"color: #000000;\"><strong style=\"line-height: 1.5;\"></strong><strong style=\"line-height: 17pt; text-indent: 32.15pt; color: #222222;\"><span style=\"font-size: 15pt; font-family: 宋体;\">【六大特点】：</span></strong></span></p>\r\n<p style=\"margin: 12pt 0cm; font-family: tahoma, arial, 宋体, sans-serif; font-size: 14px; text-indent: 24.1pt; line-height: 17pt;\" align=\"left\"><strong><span style=\"font-size: 15pt; font-family: 宋体;\">1</span></strong><strong><span style=\"font-size: 15pt; font-family: 宋体;\">．纯天然</span></strong><strong><span style=\"font-size: 15pt; font-family: 宋体;\">:</span></strong><strong><span style=\"font-size: 15pt; font-family: 宋体; color: #c00000;\">&nbsp;</span></strong><strong><span style=\"font-size: 15pt; font-family: 宋体; color: #