INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('728','1291','material','香蓉本草丝瓜原液','<p style=\"text-align: center;\"><span style=\"color: #888888;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: x-large;\">&nbsp;&nbsp;<span style=\"color: #ffffff;\">香蓉本草丝瓜原液</span></span></span></p>\r\n<p><br /><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;丝瓜原液，采用主蔓根部30厘米处采用截蔓自流法，让丝瓜水自然流出。100%纯天然丝瓜水原液，充分的活性物质能为肌肤补充养分，从而达到美白、抗皱、补水等效果。</span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">【丝瓜原液功效】治双单蛾，又可消痰火，解毒，兼清内热，治肺痈、肺痿。加白糖煮沸内服，可镇咳，又治头痛，腹痛，感冒，脚气，水肿，酒中毒等。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></span></p>\r\n<p><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;</span></span></span></p>\r\n<p style=\"text-align: center;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp; 丝瓜原液的作用</span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">1、补水：丝瓜水含有醣类、植物粘液、维生素及矿物质等，可维持角质层正常含水量，减慢脱水与延长水合作用，能补充肌肤必要的水分，保持肌肤水嫩、细腻。</span></span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">2、保湿美白：丝瓜水含有大量天然保湿因子，不黏腻的保湿成分可有效舒缓绷紧干燥的肌肤，使面部肌肤保持湿润，富有弹性，肤色靓丽、红润、洁净、白皙。</span></span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">3、祛皱：作为药材，丝瓜水能促进新陈代谢，柔和吸附老废角质，清除深层污垢，抑制黑色素细胞生成，可有效改善粗糙有皱纹的皮肤，使肌肤回复幼嫩光洁，令肤质变得柔润亮泽。</span></span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">4、消炎：作为药材，丝瓜水具有活血通络、清热润肤作用，经常使用可有效祛除面部皮肤的黑头、粉刺等。</span></span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">5、平衡：丝瓜原液性甘，温和，可调节面部皮脂分泌，对面部皮肤具有清爽，收敛，镇静，平衡的功能。</span></span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">使用方法：1.如果面部有热痘，取3&mdash;5毫升丝瓜原液直接涂抹患处。</span></span></span></span></p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.直接喷洒在面部，用手轻拍面部，使丝瓜水均匀地吸收。</span></span></span></span></p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3.做丝瓜原液面膜：取5毫升丝瓜原液对面膜纸进行浸泡，把面膜覆盖整个脸部。</span></span></span></span></p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: #888888;\"></span></span></p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">注意事项：1.本产品属于纯天然植物萃取液，不含任何防腐剂和化学添加剂。</span></span></span></span></p>\r\n<p><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.本产品放置时间久了会自然产生微量悬浮物，属自然现象，不影响使用功效。</span></span></span></span></p>\r\n<p><br /><span style=\"font-size: medium;\"><span style=\"color: #888888;\"><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;市场零售价：30元/250ML/瓶&nbsp;<br /></span></span></span></span></p>','2625','保健品	保健功效	祛痤疮','振华堂','0','','','1','0',null,'1409199266','1409347123','819','data/files/store_1291/goods_90/small_201408291008108128.jpg','1','2595','2616','2625','0','18.00','12','');
