INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('729','1291','material','眼病滴液和眼贴','<p style=\"TEXT-ALIGN: center\"><span style=\"font-size: x-large;\"><span style=\"color: #ffffff;\">眼病滴液和眼贴</span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 滴眼液和眼贴膜主要是消炎杀菌、通窍明目、通经活络的功效，眼贴膜可以促进滴眼液中有效成分的渗透吸收，直达穴位，用后能促进眼部新陈代谢，加快眼部血液循环，眼贴去除黑眼圈的效果非常好，贴上5分钟后就会有清凉透心的感觉。 </span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 滴眼液和眼贴膜主要是消炎杀菌、通窍明目、通经活络的功效，眼贴膜可以促进滴眼液中有效成分的渗透吸收，直达穴位，用后能促进眼部新陈代谢，加快眼部血液循环，眼贴去除黑眼圈的效果非常好，贴上5分钟后就会有清凉透心的感觉。 </span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 滴眼液和眼贴成分：纯中药</span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;【治疗范围】视力不良者、过度用眼者及视力下降、眼睛干涩、发痒、酸胀、疼痛、白内障、玻璃体浑浊、青光眼视神经萎缩、眼底出血后遗症、视网膜色素变性、老花眼、发痒、分泌物多、迎风流泪、攀睛胬肉，慢性结膜炎、沙眼、角膜炎、角膜云翳、角膜白班、慢性泪囊炎、近视、弱视、散光、沙眼、视力疲劳导致的眼睛酸胀、疼痛、流泪等各种眼部慢性炎症。</span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp; 【使用方法】闭上眼睛，将两片眼贴分别贴于眼部，敷至5-8分钟时，双手掌心搓热，轻压眼部，可促进药物吸收，配合轻柔太阳穴，眼贴快干时把袋子里的眼贴药水倒在眼贴上面，每次闭目休息30分钟睁开双眼，同时转动眼球数次，用力眨巴眼睛数次，每天1-2次。晚上使用先滴入眼药水后再外贴眼贴膜直接上床休息效果更佳，记住：沙眼不用眼贴。</span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 【好转反应】刚开始使用眼滴液会有个别可能会出现红痒、干涩、早上眼屎增多等现象，这是正邪相斗 眼睛有些不适应，这是正常现象，不用担心。中医理论中有&ldquo;目得血而视&rdquo;的理论，只有血液循环畅通，才能使眼部吸收到所需要的营养成份。</span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 【建 议】配合调肝肾的中药一起服用。</span></span></p>\r\n<p><span style=\"color: #ffffff;\">&nbsp;</span></p>\r\n<p><span style=\"font-size: large;\"><span style=\"color: #ffffff;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;市场零售价：400元/疗程&nbsp;&nbsp;&nbsp;&nbsp; <br /></span></span></p>','2815','保健品	保健功效	其它','振华堂','0','','','1','0',null,'1409199453','1409343706','820','data/files/store_1291/goods_130/small_201408290952109170.JPG','1','2595','2616','2815','0','240.00','160','');
