INSERT INTO ecm_goods ( `goods_id`, `store_id`, `type`, `goods_name`, `description`, `cate_id`, `cate_name`, `brand`, `spec_qty`, `spec_name_1`, `spec_name_2`, `if_show`, `closed`, `close_reason`, `add_time`, `last_update`, `default_spec`, `default_image`, `recommended`, `cate_id_1`, `cate_id_2`, `cate_id_3`, `cate_id_4`, `price`, `integral`, `tags` ) VALUES  ('985','1902','material','三鼎牛肉老四川牛肉干 中华老字号五香牛肉干50g','<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 13.63636302947998px; line-height: 19.09090805053711px;\"><strong><span style=\"font-family: 宋体; color: blue; font-size: 13.5pt;\">重庆老四川大酒楼是重庆市商务集团饮食服务有限公司旗下的知名中餐品牌企业。</span></strong></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 13.63636302947998px; line-height: 19.09090805053711px;\"><strong><span style=\"font-family: 宋体; color: blue; font-size: 13.5pt;\">老四川大酒楼创建于上世纪三十年代初，最初是一个专营牛肉食品的街边无名小摊，摊主叫钟益凤，由于特色突出，口味上乘，很受食客青睐。重庆成为抗战陪都时，有个经常光顾小摊的新民晚报记者给钟益凤建议以&ldquo;老四川&rdquo;作为食摊的名号，理由是这三字琅琅上口、通俗易懂。钟益凤欣然接受，&ldquo;老四川&rdquo;一直沿用至今。据著名历史小说《红岩》137页记载：半个世纪以前，老四川已闻名陪都。</span></strong></p>\r\n<p style=\"margin: 0cm 0cm 0pt; font-family: tahoma, arial, 宋体, sans-serif; font-size: 13.63636302947998px; line-height: 19.09090805053711px;\"><strong><span style=\"font-family: 宋体; color: blue; font-size: 13.5pt;\">经过七十多年的历史演变，昔日街边小摊，现已成为风味特色突出、经济效益上乘的名店望楼。1993年&ldquo;老四川&rdquo;大酒楼被国家内贸部授予重庆中餐业独一无二的&ldquo;中华老字号&rdquo;，其&ldquo;老四川&rdquo;商标也在国家工商总局商标局注册。2004年荣获国际餐饮名店。2006年再次被商务部命名为&ldquo;中华老字号&rdquo;（在第二届中华老字号品牌价值百强榜中，以1.27亿的品牌价值位列重庆地区的&ldquo;中华老字号&rdquo;第1位）。</span></strong></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 13.63636302947998px; line-height: 19.09090805053711px;\"><strong><span style=\"font-family: 宋体; color: blue; font-size: 13.5pt;\">老四川大酒楼名师荟萃，由&ldquo;中国烹饪名师&rdquo;徐劲主理厨政，传统牛肉菜品则由老四川的嫡传弟子担纲。老四川以擅长制作多款精美牛肉而闻名华夏。风味独特的灯影牛肉、精毛牛肉、白汁牛肚荣获国家烹饪最高奖&ldquo;金鼎奖&rdquo;；枸杞牛尾汤、沙参牛鞭汤、清炖牛肉汤也荣获国家级大奖；灯影牛肉、枸杞牛尾汤、沙参牛鞭汤还被评为&ldquo;中国名菜&rdquo;。2004年，在中国饭店协会、中国烹协、重庆市人民政府主办的美食节上，老四川&ldquo;金牛宴宾&rdquo;（全牛宴）被评为&ldquo;国际质量金奖&rdquo;。老四川已经成为精美牛肉菜品的代名词，是重庆餐饮界的一张亮丽名片。</span></strong></p>\r\n<p style=\"margin-top: 1.12em; margin-bottom: 1.12em; font-family: tahoma, arial, 宋体, sans-serif; font-size: 13.63636302947998px; line-height: 19.09090805053711px;\"><span style=\"color: #404040; font-size: 14px; line-height: 19px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><img style=\"vertical-align: top; float: none; color: #404040; font-size: 14px; line-height: 19px;\" src=\"http://img01.taobaocdn.com/imgextra/i1/765676642/T2EwOAXX8bXXXXXXXX_!!765676642.jpg_.webp\" alt=\"\" align=\"absMiddle\" /></p>','1709','美食特产	休闲零食	肉干/肉脯/豆干/肉类','三鼎','0','','','1','0',null,'1413674902','1413936219','1077','data/files/store_1902/goods_71/small_201410191527514119.jpg','1','1705','1706','1709','0','4.00','10','');
